 749// Ensure cleanup on process exit
 750process.on('exit', () => {
 751    pluginDiscoveryPool.cleanup();
 752});
 753
 754process.on('SIGINT', () => {
 755    console.log('\n[Maven Plugin Discovery] Received SIGINT, cleaning up...');
 756    pluginDiscoveryPool.cleanup();
 757    process.exit(0);
 758});
 759
 760process.on('SIGTERM', () => {
 761    console.log('\n[Maven Plugin Discovery] Received SIGTERM, cleaning up...');
 762    pluginDiscoveryPool.cleanup();
 763    process.exit(0);
 764});
 765
 766process.on('uncaughtException', (error) => {
 767    console.error('[Maven Plugin Discovery] Uncaught exception, cleaning up...', error);
 768    pluginDiscoveryPool.cleanup();
 769    process.exit(1);
 770});
 771
 772process.on('unhandledRejection', (reason, promise) => {
 773    console.error('[Maven Plugin Discovery] Unhandled rejection, cleaning up...', reason);
 774    pluginDiscoveryPool.cleanup();
 775});
