1080// Optimized version using coordinate map instead of linear search
1081function createProjectDependenciesOptimized(
1082    projectInfo: MavenProjectInfo,
1083    coordinateMap: Map<string, string>,
1084    includeTestDependencies: boolean = false
1085): string[] {
1086    if (!projectInfo.dependencies) return [];
1087
1088    const dependencies: string[] = [];
1089
1090    for (const dep of projectInfo.dependencies) {
1091        // Include compile, runtime, and optionally test dependencies
1092        const includeThisDep = dep.scope === 'compile' ||
1093            dep.scope === 'runtime' ||
1094            !dep.scope || // Default scope is compile
1095            (includeTestDependencies && dep.scope === 'test');
1096
1097        if (!includeThisDep) continue;
1098
1099        // Fast coordinate lookup
1100        const coordinate = `${dep.groupId}.${dep.artifactId}`;
1101        const dependencyProjectName = coordinateMap.get(coordinate);
1102        if (dependencyProjectName) {
1103            dependencies.push(dependencyProjectName);
1104        }
1105    }
1106
1107    return dependencies;
1108}
