1173function findProjectByMavenCoordinates(
1174    groupId: string,
1175    artifactId: string,
1176    mavenReport: MavenReport
1177): string | null {
1178    for (const [pomPath, project] of mavenReport.projects) {
1179        const projectGroupId = project.groupId ?? project.parent?.groupId;
1180        if (projectGroupId === groupId && project.artifactId === artifactId) {
1181            return `${projectGroupId}.${project.artifactId}`;
1182        }
1183    }
1184    return null;
