1187function determineProjectType(projectInfo: MavenProjectInfo): 'application' | 'library' {
1188    // POM packaging usually indicates an aggregator/parent project
1189    if (projectInfo.packaging === 'pom') {
1190        return 'library';
1191    }
1192
1193    // Common application packaging types
1194    if (['jar', 'war', 'ear'].includes(projectInfo.packaging)) {
1195        // Check if it has a main class or is a web application
1196        if (projectInfo.packaging === 'war' || projectInfo.packaging === 'ear') {
1197            return 'application';
1198        }
1199
1200        // For JAR, we need to determine if it's a library or application
1201        // Look for common patterns that indicate an application
1202        const isApplication = projectInfo.dependencies?.some(dep =>
1203            // Spring Boot applications
1204            (dep.groupId === 'org.springframework.boot' && dep.artifactId === 'spring-boot-starter') ||
1205            // Quarkus applications
1206            (dep.groupId === 'io.quarkus' && dep.artifactId.includes('quarkus-')) ||
1207            // Main class indicators
1208            dep.artifactId.includes('main') || dep.artifactId.includes('app')
1209        ) ?? false;
1210
1211        return isApplication ? 'application' : 'library';
1212    }
1213
1214    return 'library';
1215}
