417// Find a project by its Maven artifactId
418function findProjectByName(artifactId: string, mavenProjects: string[]): string | null {
419  // Try to find by directory name first (most common case)
420  for (const projectPath of mavenProjects) {
421    const dirName = projectPath.split('/').pop();
422    if (dirName === artifactId) {
423      return dirName;
424    }
425  }
426
427  // Try to find by parsing pom.xml artifactId for more accurate matching
428  for (const projectPath of mavenProjects) {
429    try {
430      const pomPath = join(workspaceRoot, projectPath, 'pom.xml');
431      if (existsSync(pomPath)) {
432        const pomContent = readFileSync(pomPath, 'utf8');
433        const artifactIdMatch = pomContent.match(/<artifactId>([^<]+)<\/artifactId>/);
434        if (artifactIdMatch && artifactIdMatch[1] === artifactId) {
435          return projectPath.split('/').pop() || artifactId;
436        }
437      }
438    } catch {
439      // Ignore parsing errors
440    }
441  }
442
443  return null;
444}
