446/**
447 * Detect framework based on dependencies and plugins in the Maven config
448 */
449function detectFramework(nxConfig: any): string {
450  const dependencies = nxConfig.implicitDependencies?.external || [];
451  const targets = nxConfig.targets || {};
452
453  // Check for Spring Boot
454  if (dependencies.some((dep: string) => dep.includes('spring-boot')) ||
455      Object.values(targets).some((target: any) =>
456        target.options?.command?.includes('spring-boot'))) {
457    return 'spring-boot';
458  }
459
460  // Check for Quarkus
461  if (dependencies.some((dep: string) => dep.includes('quarkus')) ||
462      Object.values(targets).some((target: any) =>
463        target.options?.command?.includes('quarkus'))) {
464    return 'quarkus';
465  }
466
467  // Check for Micronaut
468  if (dependencies.some((dep: string) => dep.includes('micronaut'))) {
469    return 'micronaut';
470  }
471
472  // Check for Jakarta EE
473  if (dependencies.some((dep: string) => dep.includes('jakarta'))) {
474    return 'jakarta-ee';
475  }
476
477  // Check for Maven specific patterns
478  if (Object.keys(targets).includes('serve')) {
479    return 'web-app';
480  }
481
482  return 'maven';
483}
