556/**
557 * Create a target configuration for a Maven phase (aggregator that depends on its own goals)
558 */
559function createPhaseTarget(phase: string, options: MavenPluginOptions, goalsByPhase: Record<string, string[]>): TargetConfiguration | null {
560  const baseInputs = ['{projectRoot}/pom.xml'];
561  const baseCommand = `${options.mavenExecutable} ${phase}`;
562  
563  // Define phase-specific configurations
564  const phaseConfig: Record<string, Partial<TargetConfiguration>> = {
565    'clean': {
566      inputs: baseInputs,
567      outputs: [],
568    },
569    'validate': {
570      inputs: baseInputs,
571      outputs: [],
572    },
573    'compile': {
574      inputs: ['{projectRoot}/src/main/**/*', ...baseInputs],
575      outputs: ['{projectRoot}/target/classes/**/*'],
576    },
577    'test-compile': {
578      inputs: ['{projectRoot}/src/test/**/*', '{projectRoot}/src/main/**/*', ...baseInputs],
579      outputs: ['{projectRoot}/target/test-classes/**/*'],
580    },
581    'test': {
582      inputs: ['{projectRoot}/src/test/**/*', '{projectRoot}/src/main/**/*', ...baseInputs],
583      outputs: ['{projectRoot}/target/surefire-reports/**/*', '{projectRoot}/target/test-classes/**/*'],
584    },
585    'package': {
586      inputs: ['{projectRoot}/src/**/*', ...baseInputs],
587      outputs: ['{projectRoot}/target/*.jar', '{projectRoot}/target/*.war'],
588    },
589    'verify': {
590      inputs: ['{projectRoot}/src/**/*', ...baseInputs],
591      outputs: ['{projectRoot}/target/**/*', '{projectRoot}/target/failsafe-reports/**/*'],
592    },
593    'install': {
594      inputs: ['{projectRoot}/src/**/*', ...baseInputs],
595      outputs: ['{projectRoot}/target/**/*'],
596    },
597    'deploy': {
598      inputs: ['{projectRoot}/src/**/*', ...baseInputs],
599      outputs: ['{projectRoot}/target/**/*'],
600    },
601    'site': {
602      inputs: ['{projectRoot}/src/**/*', ...baseInputs],
603      outputs: ['{projectRoot}/target/site/**/*'],
604    },
605    'integration-test': {
606      inputs: ['{projectRoot}/src/**/*', ...baseInputs],
607      outputs: ['{projectRoot}/target/failsafe-reports/**/*'],
608    },
609  };
610  
611  const config = phaseConfig[phase] || {
612    inputs: baseInputs,
613    outputs: [],
614  };
615  
616  const targetConfig: TargetConfiguration = {
617    executor: '@nx/run-commands:run-commands',
618    options: {
619      command: baseCommand,
620      cwd: '{projectRoot}',
621    },
622    metadata: {
623      type: 'phase',
624      phase: phase,
625      technologies: ['maven'],
626      description: `Maven lifecycle phase: ${phase} (aggregator)`,
627    },
628    ...config,
629  };
630  
631  // Phase depends on all its own goals
632  const goalsInPhase = goalsByPhase[phase] || [];
633  if (goalsInPhase.length > 0) {
634    targetConfig.dependsOn = goalsInPhase;
635  }
636  
637  return targetConfig;
638}
