640/**
641 * Create a target configuration for a plugin goal using pre-calculated dependencies
642 */
643function createGoalTarget(goalInfo: any, options: MavenPluginOptions, goalDependencies: Record<string, string[]>, crossProjectDependencies: Record<string, string[]>): TargetConfiguration | null {
644  const { pluginKey, goal, targetType, phase } = goalInfo;
645  
646  // Create the Maven command
647  const [groupId, artifactId] = pluginKey.split(':');
648  const command = `${options.mavenExecutable} ${groupId}:${artifactId}:${goal}`;
649  
650  // Create a more user-friendly description
651  const pluginName = artifactId.replace('-maven-plugin', '').replace('-plugin', '');
652  let description = `${pluginName}:${goal}`;
653  
654  // Add framework-specific descriptions
655  if (pluginKey.includes('quarkus')) {
656    switch (goal) {
657      case 'dev':
658        description = 'Start Quarkus development mode';
659        break;
660      case 'build':
661        description = 'Build Quarkus application';
662        break;
663      case 'generate-code':
664        description = 'Generate Quarkus code';
665        break;
666      case 'test':
667        description = 'Run Quarkus tests';
668        break;
669      default:
670        description = `Quarkus ${goal}`;
671    }
672  } else if (pluginKey.includes('spring-boot')) {
673    switch (goal) {
674      case 'run':
675        description = 'Start Spring Boot application';
676        break;
677      case 'build-image':
678        description = 'Build Spring Boot Docker image';
679        break;
680      case 'repackage':
681        description = 'Repackage Spring Boot application';
682        break;
683      default:
684        description = `Spring Boot ${goal}`;
685    }
686  } else if (pluginKey.includes('surefire')) {
687    description = 'Run unit tests';
688  } else if (pluginKey.includes('failsafe')) {
689    description = 'Run integration tests';
690  }
691
692  // Base configuration
693  const baseConfig: TargetConfiguration = {
694    executor: '@nx/run-commands:run-commands',
695    options: {
696      command,
697      cwd: '{projectRoot}',
698    },
699    metadata: {
700      type: 'goal',
701      plugin: pluginKey,
702      goal: goal,
703      targetType: targetType,
704      phase: phase !== 'null' ? phase : undefined,
705      technologies: ['maven'],
706      description: description,
707    },
708    inputs: ['{projectRoot}/pom.xml'],
709    outputs: [],
710  };
711  
712  // Customize based on target type
713  switch (targetType) {
714    case 'serve':
715      baseConfig.inputs!.push('{projectRoot}/src/**/*');
716      break;
717    case 'build':
718      baseConfig.inputs!.push('{projectRoot}/src/**/*');
719      baseConfig.outputs = ['{projectRoot}/target/**/*'];
720      break;
721    case 'test':
722      baseConfig.inputs!.push('{projectRoot}/src/test/**/*', '{projectRoot}/src/main/**/*');
723      baseConfig.outputs = ['{projectRoot}/target/surefire-reports/**/*'];
724      break;
725    case 'deploy':
726      baseConfig.inputs!.push('{projectRoot}/src/**/*');
727      break;
728    case 'utility':
729      baseConfig.inputs!.push('{projectRoot}/src/**/*');
730      break;
731  }
732  
733  // Get goal-to-goal dependencies from Java analyzer
734  const targetName = goalInfo.targetName || goal;
735  const goalDeps = goalDependencies[targetName] || [];
736  
737  // Get cross-project dependencies for this target
738  const crossProjectDeps = crossProjectDependencies[targetName] || [];
739  
740  // Merge goal dependencies and cross-project dependencies
741  const allDependencies = [...goalDeps, ...crossProjectDeps];
742  if (allDependencies.length > 0) {
743    // Resolve cross-project dependencies with fallbacks
744    const resolvedDeps = allDependencies.map(dep => {
745      if (dep.includes(':')) {
746        return resolveCrossProjectDependency(dep);
747      }
748      return dep;
749    }).filter(dep => dep !== null);
750    
751    if (resolvedDeps.length > 0) {
752      baseConfig.dependsOn = resolvedDeps;
753    }
754  }
755  
756  return baseConfig;
757}
758
759/**
760 * Resolve cross-project dependency with fallback support
761 * Handles dependencies like "projectA:package|compile|validate"
762 */
763function resolveCrossProjectDependency(dependency: string): string {
764  if (!dependency.includes(':')) {
765    return dependency; // Not a cross-project dependency
766  }
767  
768  const [project, fallbackChain] = dependency.split(':', 2);
769  if (!fallbackChain || !fallbackChain.includes('|')) {
770    return dependency; // No fallback, return as-is
771  }
772  
773  // For now, just return the first target in the fallback chain
774  // In a complete implementation, this would check which targets actually exist
775  const targets = fallbackChain.split('|');
776  return `${project}:${targets[0]}`;
777}
778
779/**
780 * Get dependencies for a Maven phase
781 */
782function getPhaseDependencies(phase: string): string[] {
783  const phaseDependencies: Record<string, string[]> = {
784    'test': ['compile'],
785    'package': ['test'],
786    'verify': ['package'],
787    'install': ['verify'],
788    'deploy': ['install'],
789    'integration-test': ['package'],
790  };
791  
792  return phaseDependencies[phase] || [];
793}
