149async function runMavenAnalysis(options: MavenPluginOptions): Promise<any> {
150  const outputFile = join(workspaceDataDirectory, 'maven-analysis.json');
151
152  // Check if verbose mode is enabled
153  const isVerbose = options.verbose || process.env.NX_VERBOSE_LOGGING === 'true' || process.argv.includes('--verbose');
154
155  // Skip Java analyzer check during testing to avoid git HEAD issues
156  if (process.env.NODE_ENV !== 'test') {
157    // Check if Java analyzer is available
158    if (!findJavaAnalyzer()) {
159      throw new Error('Maven analyzer not found. Please ensure maven-plugin is compiled.');
160    }
161  }
162
163  if (isVerbose) {
164    console.log(`Running Maven analysis with verbose logging enabled...`);
165    console.log(`Maven executable: ${options.mavenExecutable}`);
166    console.log(`Output file: ${outputFile}`);
167  }
168
169  // Build Maven command arguments
170  // Use the custom nx:analyze goal from our Java Maven plugin
171  const mavenArgs = [
172    'io.quarkus:maven-plugin:999-SNAPSHOT:analyze',
173    `-Dnx.outputFile=${outputFile}`,
174    `-Dnx.verbose=${isVerbose}`
175  ];
176
177  // Always use quiet mode to suppress expected reactor dependency warnings
178  // These warnings are normal in large multi-module projects and don't affect functionality
179  mavenArgs.push('-q');
180
181  if (isVerbose) {
182    console.log(`Executing Maven command: ${options.mavenExecutable} ${mavenArgs.join(' ')}`);
183    console.log(`Working directory: ${workspaceRoot}`);
184  }
185
186  // Run Maven plugin
187  await new Promise<void>((resolve, reject) => {
188    const child = spawn(options.mavenExecutable, mavenArgs, {
189      cwd: workspaceRoot,
190      stdio: 'inherit'
191    });
192
193    // No timeout for very large codebases like Quarkus
194    child.on('close', (code) => {
195      if (isVerbose) {
196        console.log(`Maven process completed with exit code: ${code}`);
197      }
198      if (code === 0) {
199        if (isVerbose) {
200          console.log(`Maven analysis completed successfully`);
201        }
202        resolve();
203      } else {
204        reject(new Error(`Maven process exited with code ${code}`));
205      }
206    });
207
208    child.on('error', (error) => {
209      reject(new Error(`Failed to spawn Maven process: ${error.message}`));
210    });
211  });
212
213  // Read and return JSON result
214  if (!existsSync(outputFile)) {
215    throw new Error(`Output file not found: ${outputFile}`);
216  }
217
218  const jsonContent = readFileSync(outputFile, 'utf8');
219  return JSON.parse(jsonContent);
220}
