  1import com.google.gson.Gson;
  2import com.google.gson.GsonBuilder;
  3import org.apache.maven.shared.invoker.*;
  4
  5import java.io.File;
  6import java.util.*;
  7
  8/**
  9 * Nx Maven Batch Executor - Executes multiple Maven goals in a single session
 10 * while maintaining proper artifact context and providing detailed per-goal results.
 11 */
 12public class NxMavenBatchExecutor {
 13
 14    private static final Gson gson = new GsonBuilder().setPrettyPrinting().create();
 15
 16    public static void main(String[] args) {
 17        if (args.length < 3) {
 18            System.err.println("Usage: java NxMavenBatchExecutor <goals> <workspaceRoot> <projects> [verbose]");
 19            System.err.println("Example: java NxMavenBatchExecutor \"maven-jar-plugin:jar,maven-install-plugin:install\" \"/workspace\" \".,module1,module2\" true");
 20            System.exit(1);
 21        }
 22
 23        String goalsList = args[0];
 24        String workspaceRoot = args[1];
 25        String projectsList = args[2];
 26        boolean verbose = args.length > 3 && Boolean.parseBoolean(args[3]);
 27
 28        try {
 29            List<String> goals = Arrays.asList(goalsList.split(","));
 30            List<String> projects = Arrays.asList(projectsList.split(","));
 31            BatchExecutionResult result = executeBatch(goals, workspaceRoot, projects, verbose);
 32            
 33            // Output JSON result for Nx to parse
 34            System.out.println(gson.toJson(result));
 35            
 36            // Exit with appropriate code
 37            System.exit(result.isOverallSuccess() ? 0 : 1);
 38            
 39        } catch (Exception e) {
 40            BatchExecutionResult errorResult = new BatchExecutionResult();
 41            errorResult.setOverallSuccess(false);
 42            errorResult.setErrorMessage("Batch executor failed: " + e.getMessage());
 43            
 44            System.err.println(gson.toJson(errorResult));
 45            System.exit(1);
 46        }
 47    }
 48
 49    /**
 50     * Execute multiple Maven goals across multiple projects in a single session
 51     */
 52    public static BatchExecutionResult executeBatch(List<String> goals, String workspaceRoot, List<String> projects, boolean verbose) {
 53        BatchExecutionResult batchResult = new BatchExecutionResult();
 54        batchResult.setOverallSuccess(true);
 55        
 56        try {
 57            File workspaceDir = new File(workspaceRoot);
 58            File rootPomFile = new File(workspaceDir, "pom.xml");
 59            
 60            if (!rootPomFile.exists()) {
 61                throw new RuntimeException("Root pom.xml not found in workspace: " + workspaceRoot);
 62            }
 63
 64            long batchStartTime = System.currentTimeMillis();
 65
 66            // Execute all goals across all projects in single Maven invoker session
 67            GoalExecutionResult batchGoalResult = executeMultiProjectGoalsWithInvoker(goals, projects, workspaceDir, rootPomFile, verbose);
 68            batchResult.addGoalResult(batchGoalResult);
 69            batchResult.setOverallSuccess(batchGoalResult.isSuccess());
 70            if (!batchGoalResult.isSuccess()) {
 71                batchResult.setErrorMessage("Multi-project batch goal execution failed");
 72            }
 73            
 74            long batchDuration = System.currentTimeMillis() - batchStartTime;
 75            batchResult.setTotalDurationMs(batchDuration);
 76            
 77        } catch (Exception e) {
 78            batchResult.setOverallSuccess(false);
 79            batchResult.setErrorMessage("Batch execution failed: " + e.getMessage());
 80        }
 81        
 82        return batchResult;
 83    }
 84
 85    /**
 86     * Execute multiple goals across multiple projects using Maven Invoker API
 87     */
 88    private static GoalExecutionResult executeMultiProjectGoalsWithInvoker(List<String> goals, List<String> projects, File workspaceDir, File rootPomFile, boolean verbose) {
 89        GoalExecutionResult goalResult = new GoalExecutionResult();
 90        goalResult.setGoal(String.join(",", goals) + " (across " + projects.size() + " projects)");
 91        
 92        long startTime = System.currentTimeMillis();
 93        
 94        try {
 95            // Capture output
 96            List<String> outputLines = new ArrayList<>();
 97            List<String> errorLines = new ArrayList<>();
 98            
 99            Invoker invoker = new DefaultInvoker();
100            
101            // Find Maven executable
102            String mavenHome = System.getenv("MAVEN_HOME");
103            if (mavenHome != null) {
104                invoker.setMavenHome(new File(mavenHome));
105            }
106            
107            InvocationRequest request = new DefaultInvocationRequest();
108            request.setPomFile(rootPomFile);
109            request.setBaseDirectory(workspaceDir);
110            request.setGoals(goals); // Execute all goals in single Maven session
111            
112            // Use Maven's -pl option to specify which projects to build
113            // Convert project paths to Maven module identifiers
114            List<String> projectList = new ArrayList<>();
115            for (String project : projects) {
116                if (".".equals(project)) {
117                    // Root project - use the artifact ID from root pom
118                    projectList.add(".");
119                } else {
120                    // Child module - use relative path
121                    projectList.add(project);
122                }
123            }
124            
125            if (!projectList.isEmpty() && !projectList.equals(Arrays.asList("."))) {
126                // Only use -pl if we're not building everything (i.e., not just root)
127                String projectsArg = String.join(",", projectList);
128                request.setProjects(Arrays.asList(projectsArg.split(",")));
129            }
130            
131            // Set output handlers
132            request.setOutputHandler(line -> {
133                outputLines.add(line);
134                if (verbose) {
135                    System.out.println("[MULTI-PROJECT] " + line);
136                }
137            });
138            
139            request.setErrorHandler(line -> {
140                errorLines.add(line);
141                if (verbose) {
142                    System.err.println("[MULTI-PROJECT ERROR] " + line);
143                }
144            });
145
146            if (verbose) {
147                System.out.println("Executing goals: " + String.join(", ", goals));
148                System.out.println("Across projects: " + String.join(", ", projects));
149                System.out.println("Working directory: " + workspaceDir.getAbsolutePath());
150            }
151
152            // Execute all goals across all projects in single Maven reactor session
153            InvocationResult result = invoker.execute(request);
154            
155            long duration = System.currentTimeMillis() - startTime;
156            
157            goalResult.setSuccess(result.getExitCode() == 0);
158            goalResult.setDurationMs(duration);
159            goalResult.setOutput(outputLines);
160            goalResult.setErrors(errorLines);
161            goalResult.setExitCode(result.getExitCode());
162            
163            if (result.getExecutionException() != null) {
164                goalResult.setErrorMessage(result.getExecutionException().getMessage());
165            }
166            
167        } catch (Exception e) {
168            long duration = System.currentTimeMillis() - startTime;
169            goalResult.setSuccess(false);
170            goalResult.setDurationMs(duration);
171            goalResult.setErrorMessage("Multi-project goal execution exception: " + e.getMessage());
172            goalResult.setErrors(Arrays.asList(e.getMessage()));
173        }
174        
175        return goalResult;
176    }
