  1import { ExecutorContext, logger, TaskGraph } from '@nx/devkit';
  2import { execSync } from 'child_process';
  3import { join } from 'path';
  4import { existsSync, writeFileSync } from 'fs';
  5
  6export interface MavenBatchExecutorOptions {
  7  goals: string[];
  8  projectRoot?: string;
  9  verbose?: boolean;
 10  mavenPluginPath?: string;
 11  timeout?: number;
 12  outputFile?: string;
 13  failOnError?: boolean;
 14}
 15
 16export interface MavenGoalResult {
 17  goal: string;
 18  success: boolean;
 19  durationMs: number;
 20  exitCode: number;
 21  output: string[];
 22  errors: string[];
 23}
 24
 25export interface MavenBatchResult {
 26  overallSuccess: boolean;
 27  totalDurationMs: number;
 28  errorMessage?: string;
 29  goalResults: MavenGoalResult[];
 30}
 31
 32export interface ExecutorResult {
 33  success: boolean;
 34  terminalOutput: string;
 35  output?: MavenBatchResult;
 36  error?: string;
 37}
 38
 39// Regular executor for single task execution
 40export default async function runExecutor(
 41  options: MavenBatchExecutorOptions,
 42  context: ExecutorContext
 43): Promise<ExecutorResult> {
 44  const {
 45    goals,
 46    projectRoot = '.',
 47    verbose = false,
 48    mavenPluginPath = 'maven-plugin',
 49    timeout = 300000,
 50    outputFile,
 51    failOnError = true
 52  } = options;
 53
 54  if (!goals || goals.length === 0) {
 55    const error = 'At least one Maven goal must be specified';
 56    logger.error(error);
 57    return { success: false, terminalOutput: error, error };
 58  }
 59
 60  // Resolve paths
 61  const workspaceRoot = context.root;
 62  const pluginDir = join(workspaceRoot, mavenPluginPath);
 63  const projectDir = join(workspaceRoot, projectRoot);
 64
 65  // Validate plugin directory
 66  if (!existsSync(pluginDir)) {
 67    const error = `Maven plugin directory not found: ${pluginDir}`;
 68    logger.error(error);
 69    return { success: false, terminalOutput: error, error };
 70  }
 71
 72  // Validate project directory
 73  if (!existsSync(projectDir)) {
 74    const error = `Project directory not found: ${projectDir}`;
 75    logger.error(error);
 76    return { success: false, terminalOutput: error, error };
 77  }
 78
 79  // Check for batch executor
 80  const batchExecutorClasspath = join(pluginDir, 'target/classes');
 81  const dependencyPath = join(pluginDir, 'target/dependency');
 82  
 83  if (!existsSync(batchExecutorClasspath)) {
 84    const error = `Maven plugin not compiled. Run 'mvn compile' in ${pluginDir}`;
 85    logger.error(error);
 86    return { success: false, terminalOutput: error, error };
 87  }
 88
 89  if (!existsSync(dependencyPath)) {
 90    const error = `Maven dependencies not copied. Run 'mvn dependency:copy-dependencies' in ${pluginDir}`;
 91    logger.error(error);
 92    return { success: false, terminalOutput: error, error };
 93  }
 94
 95  try {
 96    const goalsString = goals.join(',');
 97    const verboseFlag = verbose ? 'true' : 'false';
 98    
 99    // Build command with new signature: goals, workspaceRoot, projects, verbose
100    const classpath = `${batchExecutorClasspath}:${dependencyPath}/*`;
101    const command = `java -Dmaven.multiModuleProjectDirectory="${workspaceRoot}" -cp "${classpath}" NxMavenBatchExecutor "${goalsString}" "${workspaceRoot}" "${projectRoot}" ${verboseFlag}`;
102
103    if (verbose) {
104      logger.info(`Executing Maven batch command:`);
105      logger.info(`  Goals: ${goals.join(', ')}`);
106      logger.info(`  Project: ${projectRoot}`);
107      logger.info(`  Working directory: ${pluginDir}`);
108      logger.info(`  Command: ${command}`);
109    }
110
111    // Execute the batch command
112    const startTime = Date.now();
113    const output = execSync(command, {
114      cwd: pluginDir,
115      encoding: 'utf-8',
116      timeout: timeout,
117      maxBuffer: 10 * 1024 * 1024 // 10MB buffer
118    });
119
120    const duration = Date.now() - startTime;
121    
122    // Parse JSON output from batch executor
123    // The output may contain Maven warnings before the JSON, so find the JSON part
124    let result: MavenBatchResult;
125    try {
126      // Find the JSON output (starts with '{' and ends with '}')
127      const lines = output.trim().split('\n');
128      let jsonStart = -1;
129      let jsonEnd = -1;
130      
131      // Find the start of JSON
132      for (let i = 0; i < lines.length; i++) {
133        if (lines[i].trim().startsWith('{')) {
134          jsonStart = i;
135          break;
136        }
137      }
138      
139      // Find the end of JSON (last '}')
140      for (let i = lines.length - 1; i >= 0; i--) {
141        if (lines[i].trim().endsWith('}')) {
142          jsonEnd = i;
143          break;
144        }
145      }
146      
147      if (jsonStart === -1 || jsonEnd === -1) {
148        throw new Error('No JSON output found');
149      }
150      
151      const jsonOutput = lines.slice(jsonStart, jsonEnd + 1).join('\n');
152      result = JSON.parse(jsonOutput);
153    } catch (parseError: any) {
154      const error = `Failed to parse batch executor output: ${parseError?.message || parseError}`;
155      logger.error(error);
156      logger.debug(`Raw output: ${output}`);
157      return { success: false, terminalOutput: error, error };
158    }
159
160    // Log results
161    if (verbose || !result.overallSuccess) {
162      logger.info(`Maven batch execution completed in ${duration}ms`);
163      logger.info(`Overall success: ${result.overallSuccess}`);
164      
165      if (result.errorMessage) {
166        logger.error(`Error: ${result.errorMessage}`);
167      }
168
169      result.goalResults.forEach((goalResult, index) => {
170        const status = goalResult.success ? '' : '';
171        logger.info(`${status} Goal ${index + 1}: ${goalResult.goal} (${goalResult.durationMs}ms)`);
172        
173        if (!goalResult.success && goalResult.errors.length > 0) {
174          goalResult.errors.forEach(error => logger.error(`  Error: ${error}`));
175        }
176        
177        if (verbose && goalResult.output.length > 0) {
178          logger.debug(`  Output: ${goalResult.output.slice(-5).join('\n  ')}`); // Last 5 lines
179        }
180      });
181    }
182
183    // Write output file if specified
184    if (outputFile) {
185      const outputPath = join(workspaceRoot, outputFile);
186      writeFileSync(outputPath, JSON.stringify(result, null, 2));
187      if (verbose) {
188        logger.info(`Results written to: ${outputPath}`);
189      }
190    }
191
192    // Determine success
193    const success = result.overallSuccess || !failOnError;
194    
195    if (!success && failOnError) {
196      logger.error(`Maven batch execution failed`);
197      if (result.errorMessage) {
198        logger.error(result.errorMessage);
199      }
200    }
201
202    return {
203      success,
204      terminalOutput: result.goalResults.map(r => r.output.join('\n')).join('\n'),
205      output: result,
206      error: result.errorMessage
207    };
208
209  } catch (error: any) {
210    const errorMessage = error?.message || String(error);
211    logger.error(`Maven batch executor failed: ${errorMessage}`);
212    
213    if (verbose) {
214      logger.debug(`Error details:`, error);
215    }
216
217    return {
218      success: false,
219      terminalOutput: errorMessage,
220      error: errorMessage
221    };
222  }
223}
224
225// Simplified Nx batch executor - collect all goals and projects from task graph and execute in one batch
226export async function batchMavenExecutor(
227  taskGraph: TaskGraph,
228  inputs: Record<string, MavenBatchExecutorOptions>
229): Promise<Record<string, { success: boolean; terminalOutput: string }>> {
230  const results: Record<string, { success: boolean; terminalOutput: string }> = {};
231  
232  try {
233    // Collect ALL goals and projects from ALL tasks in the task graph
234    const allGoals: string[] = [];
235    const allProjects: string[] = [];
236    const taskIds: string[] = [];
237    let verbose = false;
238    let commonOptions: MavenBatchExecutorOptions | undefined;
239    
240    // Extract goals and project roots from each task in the task graph
241    for (const [taskId, options] of Object.entries(inputs)) {
242      const task = taskGraph.tasks[taskId];
243      
244      if (task) {
245        // Get goals from the task's configuration options
246        const taskGoals = options.goals || [];
247        allGoals.push(...taskGoals);
248        
249        // Get project root (use from options or task target project)
250        const projectRoot = options.projectRoot || task.target.project || '.';
251        allProjects.push(projectRoot);
252        
253        taskIds.push(taskId);
254        
255        // Use first task's options as base, enable verbose if any task requests it
256        if (!commonOptions) commonOptions = options;
257        if (options.verbose) verbose = true;
258      }
259    }
260    
261    // Remove duplicate goals and projects
262    const uniqueGoals = Array.from(new Set(allGoals));
263    const uniqueProjects = Array.from(new Set(allProjects));
264    
265    // If no tasks or goals, return empty results
266    if (taskIds.length === 0 || uniqueGoals.length === 0) {
267      return results;
268    }
269    
270    // Execute ALL unique goals across ALL unique projects in a single batch
271    const batchOptions = { ...commonOptions!, verbose };
272    const batchResult = await executeMultiProjectMavenBatch(uniqueGoals, uniqueProjects, batchOptions, process.cwd());
273    
274    // All tasks get the same result (success/failure of the entire batch)
275    for (const taskId of taskIds) {
276      results[taskId] = {
277        success: batchResult.overallSuccess,
278        terminalOutput: batchResult.goalResults.map(r => r.output.join('\n')).join('\n')
279      };
280    }
281    
282    return results;
283    
284  } catch (error: any) {
285    // If batch fails, mark ALL tasks as failed
286    for (const taskId of Object.keys(inputs)) {
287      results[taskId] = {
288        success: false,
289        terminalOutput: error?.message || String(error)
290      };
291    }
292  }
293  
294  return results;
295}
296
297// Execute Maven goals across multiple projects in a single batch
298async function executeMultiProjectMavenBatch(
299  goals: string[],
300  projects: string[],
301  options: MavenBatchExecutorOptions,
302  workspaceRoot: string
303): Promise<MavenBatchResult> {
