330  const goalsString = goals.join(',');
331  const projectsString = projects.join(',');
332  const verboseFlag = verbose ? 'true' : 'false';
333  
334  // Build command with new signature: goals, workspaceRoot, projects, verbose
335  const classpath = `${batchExecutorClasspath}:${dependencyPath}/*`;
336  const command = `java -Dmaven.multiModuleProjectDirectory="${workspaceRoot}" -cp "${classpath}" NxMavenBatchExecutor "${goalsString}" "${workspaceRoot}" "${projectsString}" ${verboseFlag}`;
337
338  // Execute the batch command
339  const startTime = Date.now();
340  const output = execSync(command, {
341    cwd: pluginDir,
342    encoding: 'utf-8',
343    timeout: timeout,
344    maxBuffer: 10 * 1024 * 1024 // 10MB buffer
345  });
346
347  // Parse JSON output from batch executor
348  // The output may contain Maven warnings before the JSON, so find the JSON part
349  const lines = output.trim().split('\n');
350  let jsonStart = -1;
351  let jsonEnd = -1;
352  
353  // Find the start of JSON
354  for (let i = 0; i < lines.length; i++) {
355    if (lines[i].trim().startsWith('{')) {
356      jsonStart = i;
357      break;
358    }
359  }
360  
361  // Find the end of JSON (last '}')
362  for (let i = lines.length - 1; i >= 0; i--) {
363    if (lines[i].trim().endsWith('}')) {
364      jsonEnd = i;
365      break;
366    }
367  }
368  
369  if (jsonStart === -1 || jsonEnd === -1) {
370    throw new Error('No JSON output found');
371  }
