  1import org.apache.maven.execution.MavenSession;
  2import org.apache.maven.lifecycle.LifecycleExecutor;
  3import org.apache.maven.lifecycle.MavenExecutionPlan;
  4import org.apache.maven.lifecycle.DefaultLifecycles;
  5import org.apache.maven.plugin.MojoExecution;
  6import org.apache.maven.plugin.logging.Log;
  7import org.apache.maven.project.MavenProject;
  8
  9import java.util.*;
 10import java.util.concurrent.ConcurrentHashMap;
 11import java.util.stream.Collectors;
 12
 13/**
 14 * Service that analyzes Maven execution plans once and caches results for efficient querying.
 15 * This avoids recalculating execution plans multiple times for the same project.
 16 */
 17public class ExecutionPlanAnalysisService {
 18    
 19    private final Log log;
 20    private final boolean verbose;
 21    private final LifecycleExecutor lifecycleExecutor;
 22    private final MavenSession session;
 23    private final DefaultLifecycles defaultLifecycles;
 24    
 25    // Cache to store analysis results per project
 26    private final Map<String, ProjectExecutionAnalysis> analysisCache = new ConcurrentHashMap<>();
 27    
 28    public ExecutionPlanAnalysisService(Log log, boolean verbose, LifecycleExecutor lifecycleExecutor, MavenSession session, DefaultLifecycles defaultLifecycles) {
 29        this.log = log;
 30        this.verbose = verbose;
 31        this.lifecycleExecutor = lifecycleExecutor;
 32        this.session = session;
 33        this.defaultLifecycles = defaultLifecycles;
 34    }
 35    
 36    /**
 37     * Get or compute execution analysis for a project
 38     */
 39    public ProjectExecutionAnalysis getAnalysis(MavenProject project) {
 40        String projectKey = MavenUtils.formatProjectKey(project);
 41        return analysisCache.computeIfAbsent(projectKey, key -> analyzeProject(project));
 42    }
 43    
 44    /**
 45     * Find the phase for a specific goal
 46     */
 47    public String findPhaseForGoal(MavenProject project, String goal) {
 48        if (project == null || goal == null || goal.isEmpty()) {
 49            return null;
 50        }
 51        ProjectExecutionAnalysis analysis = getAnalysis(project);
 52        return analysis.getPhaseForGoal(goal);
 53    }
 54    
 55    
 56    /**
 57     * Get all goals for a specific phase
 58     */
 59    public List<String> getGoalsForPhase(MavenProject project, String phase) {
 60        if (project == null || phase == null) {
 61            return new ArrayList<>();
 62        }
 63        ProjectExecutionAnalysis analysis = getAnalysis(project);
 64        return analysis.getGoalsForPhase(phase);
 65    }
 66    
 67    /**
 68     * Get default lifecycle phases (validate, compile, test, package, verify, install, deploy)
 69     */
 70    public List<String> getDefaultLifecyclePhases() {
 71        return getLifecyclePhases("default");
 72    }
 73    
 74    /**
 75     * Get clean lifecycle phases (pre-clean, clean, post-clean)
 76     */
 77    public List<String> getCleanLifecyclePhases() {
 78        return getLifecyclePhases("clean");
 79    }
 80    
 81    /**
 82     * Get site lifecycle phases (pre-site, site, post-site, site-deploy)
 83     */
 84    public List<String> getSiteLifecyclePhases() {
 85        return getLifecyclePhases("site");
 86    }
 87    
 88    
 89    /**
 90     * Get lifecycle phases for a specific lifecycle ID
 91     */
 92    private List<String> getLifecyclePhases(String lifecycleId) {
 93        if (defaultLifecycles == null || lifecycleId == null) {
 94            return new ArrayList<>();
 95        }
 96
 97        try {
 98            org.apache.maven.lifecycle.Lifecycle lifecycle =
 99                defaultLifecycles.getLifeCycles().stream()
100                    .filter(lc -> lifecycleId.equals(lc.getId()))
101                    .findFirst()
102                    .orElse(null);
103
104            if (lifecycle != null && lifecycle.getPhases() != null) {
105                return new ArrayList<>(lifecycle.getPhases());
106            }
107
108            return new ArrayList<>();
109
110        } catch (Exception e) {
111            if (verbose) {
112                log.warn("Could not access Maven lifecycle '" + lifecycleId + "': " + e.getMessage());
113            }
114            return new ArrayList<>();
115        }
116    }
117    
118    /**
119     * Analyze a project's execution plans and cache the results
120     */
121    private ProjectExecutionAnalysis analyzeProject(MavenProject project) {
122        if (verbose) {
123            log.debug("Analyzing execution plans for project: " + project.getArtifactId());
124        }
125        
126        ProjectExecutionAnalysis analysis = new ProjectExecutionAnalysis();
127        
128        // Use actual lifecycle phases from Maven's lifecycle definitions
129        List<String> lifecyclePhases = getDefaultLifecyclePhases();
130        
131        for (String phase : lifecyclePhases) {
132            try {
133                if (lifecycleExecutor != null && session != null) {
134                    MavenExecutionPlan executionPlan = lifecycleExecutor.calculateExecutionPlan(session, phase);
135                    analysis.addExecutionPlan(phase, executionPlan);
136                    
137                    if (verbose) {
138                        log.debug("Analyzed " + executionPlan.getMojoExecutions().size() + 
139                                 " executions for phase: " + phase);
140                    }
141                } else {
142                    if (verbose) {
143                        log.warn("LifecycleExecutor or session is null, skipping phase: " + phase);
144                    }
145                }
146                
147            } catch (Exception e) {
148                if (verbose) {
149                    log.warn("Could not calculate execution plan for " + phase + ": " + e.getMessage());
150                }
151            }
152        }
153        
154        if (verbose) {
155            log.debug("Completed analysis for " + project.getArtifactId() + 
156                     ": " + analysis.getAllPhases().size() + " phases, " + 
157                     analysis.getAllGoals().size() + " goals");
158        }
159        
160        return analysis;
161    }
162    
163    /**
164     * Clear cache (useful for testing or memory management)
165     */
166    public void clearCache() {
167        analysisCache.clear();
168    }
169    
170    /**
171     * Get cache statistics
172     */
173    public Map<String, Object> getCacheStats() {
174        Map<String, Object> stats = new HashMap<>();
175        stats.put("cachedProjects", analysisCache.size());
176        stats.put("projectKeys", new ArrayList<>(analysisCache.keySet()));
177        return stats;
178    }
179    
180    // ========================================
181    // Target Name Utilities (moved from MavenUtils)
182    // ========================================
183    
184    /**
185     * Generate target name from artifact ID and goal
186     */
187    public static String getTargetName(String artifactId, String goal) {
188        String pluginName = normalizePluginName(artifactId);
189        return pluginName + ":" + goal;
190    }
191    
192    /**
193     * Extract goal name from target name (e.g., "compiler:compile" -> "compile")
194     */
195    public static String extractGoalFromTargetName(String targetName) {
196        if (targetName == null || !targetName.contains(":")) {
197            return targetName;
198        }
199        return targetName.substring(targetName.lastIndexOf(":") + 1);
200    }
201    
202    /**
203     * Normalize plugin artifact ID to plugin name by removing common suffixes
204     */
205    public static String normalizePluginName(String artifactId) {
206        if (artifactId == null) {
207            return null;
208        }
209        return artifactId.replace("-maven-plugin", "").replace("-plugin", "");
210    }
211    
212    /**
213     * Get common goals for well-known plugins
214     */
215    public static List<String> getCommonGoalsForPlugin(String artifactId) {
216        List<String> commonGoals = new ArrayList<>();
217        
218        if (artifactId == null) {
219            return commonGoals;
220        }
221        
222        if (artifactId.contains("compiler")) {
223            commonGoals.addAll(Arrays.asList("compile", "testCompile"));
224        } else if (artifactId.contains("surefire")) {
225            commonGoals.add("test");
226        } else if (artifactId.contains("quarkus")) {
227            commonGoals.addAll(Arrays.asList("dev", "build"));
228        } else if (artifactId.contains("spring-boot")) {
229            commonGoals.addAll(Arrays.asList("run", "repackage"));
230        }
231        
232        return commonGoals;
233    }
234    
235    /**
236     * Container for analyzed execution plan data for a single project
237     */
238    public static class ProjectExecutionAnalysis {
239        private final Map<String, String> goalToPhaseMap = new HashMap<>();
240        private final Map<String, List<String>> phaseToGoalsMap = new HashMap<>();
241        private final Set<String> allPhases = new LinkedHashSet<>();
242        private final Set<String> allGoals = new LinkedHashSet<>();
243        private final Map<String, ExecutionInfo> goalToExecutionInfo = new HashMap<>();
244        
245        /**
246         * Add execution plan data from a lifecycle endpoint
247         */
248        void addExecutionPlan(String endpoint, MavenExecutionPlan executionPlan) {
249            for (MojoExecution mojoExecution : executionPlan.getMojoExecutions()) {
250                String goal = mojoExecution.getGoal();
251                String phase = mojoExecution.getLifecyclePhase();
252                String pluginArtifactId = mojoExecution.getPlugin().getArtifactId();
253                
254                if (goal != null && phase != null) {
255                    // Store goal-to-phase mapping
256                    goalToPhaseMap.put(goal, phase);
257                    
258                    // Store detailed execution info
259                    ExecutionInfo execInfo = new ExecutionInfo(
260                        goal, phase, pluginArtifactId, 
261                        mojoExecution.getExecutionId(),
262                        mojoExecution.getPlugin().getGroupId() + ":" + pluginArtifactId
263                    );
264                    goalToExecutionInfo.put(goal, execInfo);
265                    
266                    // Handle plugin:goal format too
267                    String targetName = getTargetName(pluginArtifactId, goal);
268                    goalToPhaseMap.put(targetName, phase);
269                    goalToExecutionInfo.put(targetName, execInfo);
270                    
271                    // Track phases and goals
272                    allPhases.add(phase);
273                    allGoals.add(goal);
274                    allGoals.add(targetName);
275                    
276                    // Build phase-to-goals mapping
277                    phaseToGoalsMap.computeIfAbsent(phase, k -> new ArrayList<>()).add(targetName);
278                }
279            }
280        }
281        
282        public String getPhaseForGoal(String goal) {
283            return goalToPhaseMap.get(goal);
284        }
285        
286        public List<String> getGoalsForPhase(String phase) {
287            return phaseToGoalsMap.getOrDefault(phase, new ArrayList<>());
288        }
289        
290        public Set<String> getAllPhases() {
291            return new LinkedHashSet<>(allPhases);
292        }
293        
294        public Set<String> getAllGoals() {
295            return new LinkedHashSet<>(allGoals);
296        }
297        
298        public ExecutionInfo getExecutionInfo(String goal) {
299            return goalToExecutionInfo.get(goal);
300        }
301        
302        public Map<String, List<String>> getPhaseToGoalsMap() {
303            return new HashMap<>(phaseToGoalsMap);
304        }
305    }
306    
307    /**
308     * Detailed information about a mojo execution
309     */
310    public static class ExecutionInfo {
311        private final String goal;
312        private final String phase;
313        private final String pluginArtifactId;
314        private final String executionId;
315        private final String pluginKey;
316        
317        public ExecutionInfo(String goal, String phase, String pluginArtifactId, String executionId, String pluginKey) {
318            this.goal = goal;
319            this.phase = phase;
320            this.pluginArtifactId = pluginArtifactId;
321            this.executionId = executionId;
322            this.pluginKey = pluginKey;
323        }
324        
325        public String getGoal() { return goal; }
326        public String getPhase() { return phase; }
327        public String getPluginArtifactId() { return pluginArtifactId; }
328        public String getExecutionId() { return executionId; }
329        public String getPluginKey() { return pluginKey; }
330    }
331    
332    public List<String> getGoalOutputs(String goal, String projectRootToken, MavenProject project) {
333        // Simplified implementation without hardcoded patterns
334        return new ArrayList<>();
335    }
336}
