278    /**
279     * Find execution phase for a goal target
280     */
281    private String findExecutionPhase(MavenProject project, String targetName) {
282        String goal = ExecutionPlanAnalysisService.extractGoalFromTargetName(targetName);
283        
284        if (project.getBuildPlugins() != null) {
285            for (org.apache.maven.model.Plugin plugin : project.getBuildPlugins()) {
286                if (plugin.getExecutions() != null) {
287                    for (org.apache.maven.model.PluginExecution execution : plugin.getExecutions()) {
288                        if (execution.getGoals() != null && execution.getGoals().contains(goal)) {
289                            return execution.getPhase();
290                        }
291                    }
292                }
293            }
294        }
295        
296        return null; // Will trigger phase inference in dependency service
297    }
