299    /**
300     * Add common goals for well-known plugins
301     */
302    private void addCommonGoals(String artifactId, Set<String> goalTargets) {
303        List<String> commonGoals = ExecutionPlanAnalysisService.getCommonGoalsForPlugin(artifactId);
304        for (String goal : commonGoals) {
305            goalTargets.add(ExecutionPlanAnalysisService.getTargetName(artifactId, goal));
306        }
