310    private boolean isVerbose() {
311        String systemProp = System.getProperty("nx.verbose");
312        boolean fromParam = "true".equalsIgnoreCase(verboseStr);
313        boolean fromSystem = "true".equalsIgnoreCase(systemProp);
314        return fromParam || fromSystem;
315    }
