346            getLog().info("Performance: Processed " + reactorProjects.size() + " projects in " + 
347                         (totalTime / 1000.0) + "s (" + String.format("%.1f", projectsPerSecond) + " projects/sec)");
348        }
349        getLog().info("SUCCESS: Maven analysis completed successfully");
350    }
