  1import model.TargetConfiguration;
  2import org.apache.maven.plugin.logging.Log;
  3import org.apache.maven.project.MavenProject;
  4
  5import java.util.ArrayList;
  6import java.util.List;
  7import java.util.Map;
  8
  9/**
 10 * Service responsible for calculating target dependencies in Maven projects.
 11 * Handles phase dependencies, cross-module dependencies, and goal dependencies.
 12 */
 13public class TargetDependencyService {
 14
 15    private final Log log;
 16    private final boolean verbose;
 17    private final ExecutionPlanAnalysisService executionPlanAnalysisService;
 18
 19    public TargetDependencyService(Log log, boolean verbose, ExecutionPlanAnalysisService executionPlanAnalysisService) {
 20        this.log = log;
 21        this.verbose = verbose;
 22        this.executionPlanAnalysisService = executionPlanAnalysisService;
 23    }
 24
 25    /**
 26     * Calculate dependencies for a goal target
 27     */
 28    public List<String> calculateGoalDependencies(MavenProject project, String executionPhase,
 29                                                  String targetName, List<MavenProject> reactorProjects) {
 30        List<String> dependsOn = new ArrayList<>();
 31
 32
 33        String effectivePhase = executionPhase;
 34        if (effectivePhase == null || effectivePhase.isEmpty() || effectivePhase.startsWith("${")) {
 35            effectivePhase = executionPlanAnalysisService.findPhaseForGoal(project, ExecutionPlanAnalysisService.extractGoalFromTargetName(targetName));
 36        }
 37
 38
 39        if (effectivePhase != null && !effectivePhase.isEmpty()) {
 40            // Add dependency on preceding phase (but don't fail if this doesn't work)
 41            try {
 42                String precedingPhase = getPrecedingPhase(effectivePhase, project);
 43                if (precedingPhase != null && !precedingPhase.isEmpty()) {
 44                    dependsOn.add(precedingPhase);
 45                }
 46            } catch (Exception e) {
 47                // Continue even if preceding phase detection fails
 48            }
 49
 50            // ALWAYS add cross-module dependencies using Nx ^ syntax
 51            // Goals depend on their phase across all dependent projects
 52            dependsOn.add("^" + effectivePhase);
 53        }
 54
 55
 56        return dependsOn;
 57    }
 58
 59    /**
 60     * Calculate dependencies for a phase target
 61     */
 62    public List<String> calculatePhaseDependencies(String phase, Map<String, TargetConfiguration> allTargets,
 63                                                   MavenProject project, List<MavenProject> reactorProjects) {
 64        List<String> dependsOn = new ArrayList<>();
 65
 66        // Add dependency on preceding phase
 67        List<String> phaseDependencies = getPhaseDependencies(phase, project);
 68        dependsOn.addAll(phaseDependencies);
 69
 70        // Add dependencies on all goals that belong to this phase
 71        List<String> goalsForPhase = getGoalsForPhase(phase, allTargets);
 72        dependsOn.addAll(goalsForPhase);
 73
 74        // Add cross-module dependencies using Nx ^ syntax
 75        dependsOn.add("^" + phase);
 76
 77        return dependsOn;
 78    }
 79
 80    /**
 81     * Get phase dependencies (preceding phases)
 82     */
 83    public List<String> getPhaseDependencies(String phase, MavenProject project) {
 84        List<String> deps = new ArrayList<>();
 85        String precedingPhase = getPrecedingPhase(phase, project);
 86        if (precedingPhase != null) {
 87            deps.add(precedingPhase);
 88        }
 89        return deps;
 90    }
 91
 92    /**
 93     * Get all goals that belong to a specific phase
 94     */
 95    public List<String> getGoalsForPhase(String phase, Map<String, TargetConfiguration> allTargets) {
 96        List<String> goalsForPhase = new ArrayList<>();
 97
 98        for (Map.Entry<String, TargetConfiguration> entry : allTargets.entrySet()) {
 99            String targetName = entry.getKey();
100            TargetConfiguration target = entry.getValue();
101
102            if (target.getMetadata() != null &&
103                "goal".equals(target.getMetadata().getType()) &&
104                phase.equals(target.getMetadata().getPhase())) {
105                goalsForPhase.add(targetName);
106            }
107        }
108
109        return goalsForPhase;
110    }
111
112
113
114
115    /**
116     * Get the preceding phase in the Maven lifecycle (supports all lifecycles: default, clean, site)
117     */
118    public String getPrecedingPhase(String phase, MavenProject project) {
119        if (phase == null || phase.isEmpty()) {
120            return null;
121        }
122
123        // Check each lifecycle to find which one contains this phase
124        List<String> defaultPhases = executionPlanAnalysisService.getDefaultLifecyclePhases();
125        List<String> cleanPhases = executionPlanAnalysisService.getCleanLifecyclePhases();
126        List<String> sitePhases = executionPlanAnalysisService.getSiteLifecyclePhases();
127        
