 50â†’        this.defaultLifecyclePhases = computeLifecyclePhases("default");
 51â†’        this.cleanLifecyclePhases = computeLifecyclePhases("clean");
 52â†’        this.siteLifecyclePhases = computeLifecyclePhases("site");
 53â†’        this.phaseToLifecycleMap = computePhaseToLifecycleMap();
