import org.apache.maven.execution.MavenSession;
import org.apache.maven.lifecycle.LifecycleExecutor;
import org.apache.maven.lifecycle.MavenExecutionPlan;
import org.apache.maven.lifecycle.DefaultLifecycles;
import org.apache.maven.plugin.MojoExecution;
import org.apache.maven.plugin.logging.Log;
import org.apache.maven.project.MavenProject;

import java.util.*;
import java.util.concurrent.ConcurrentHashMap;
import java.util.stream.Collectors;

/**
 * Service that analyzes Maven execution plans on-demand with smart caching.
 * Uses lazy-loading and phase-level caching to avoid expensive calculations.
 */
public class ExecutionPlanAnalysisService {

    private final Log log;
    private final boolean verbose;
    private final LifecycleExecutor lifecycleExecutor;
    private final MavenSession session;
    private final DefaultLifecycles defaultLifecycles;

    // Lazy-loaded cache for project execution analysis
    private final Map<String, ProjectExecutionAnalysis> projectCache = new ConcurrentHashMap<>();

    // Phase-level cache for execution plans (shared across projects for similar configurations)
    private final Map<String, MavenExecutionPlan> phaseExecutionCache = new ConcurrentHashMap<>();

    // Pre-computed lifecycle information (shared across all projects)
    private final Set<String> allLifecyclePhases;
    private final List<String> defaultLifecyclePhases;
    private final List<String> cleanLifecyclePhases;
    private final List<String> siteLifecyclePhases;
    private final Map<String, org.apache.maven.lifecycle.Lifecycle> phaseToLifecycleMap;

    // Track which phases are actually needed to avoid unnecessary calculations
    private final Set<String> requestedPhases = ConcurrentHashMap.newKeySet();

    public ExecutionPlanAnalysisService(Log log, boolean verbose, LifecycleExecutor lifecycleExecutor, MavenSession session, DefaultLifecycles defaultLifecycles) {
        this.log = log;
        this.verbose = verbose;
        this.lifecycleExecutor = lifecycleExecutor;
        this.session = session;
        this.defaultLifecycles = defaultLifecycles;

        // Pre-compute only lightweight lifecycle information
        this.allLifecyclePhases = computeAllLifecyclePhases();
        this.defaultLifecyclePhases = computeLifecyclePhases("default");
        this.cleanLifecyclePhases = computeLifecyclePhases("clean");
        this.siteLifecyclePhases = computeLifecyclePhases("site");
        this.phaseToLifecycleMap = computePhaseToLifecycleMap();

        if (verbose) {
            log.info("Initialized execution plan analysis with " + allLifecyclePhases.size() + " lifecycle phases");
        }
    }

    /**
     * Get the LifecycleExecutor for use by other services
     */
    public LifecycleExecutor getLifecycleExecutor() {
        return lifecycleExecutor;
    }

    /**
     * Get the DefaultLifecycles for use by other services
     */
    public DefaultLifecycles getDefaultLifecycles() {
        return defaultLifecycles;
    }

    /**
     * Get or compute execution analysis for a project (lazy-loaded)
     */
    public ProjectExecutionAnalysis getAnalysis(MavenProject project) {
        String projectKey = MavenUtils.formatProjectKey(project);
        return projectCache.computeIfAbsent(projectKey, key -> new ProjectExecutionAnalysis(project));
    }

    /**
     * Get execution plan for a specific phase, using smart caching
     */
    public MavenExecutionPlan getExecutionPlan(MavenProject project, String phase) {
        if (phase == null || phase.isEmpty()) {
            return null;
        }

        // Track requested phases for optimization insights
        requestedPhases.add(phase);

        // Create cache key based on project configuration that affects execution plans
        String cacheKey = createExecutionPlanCacheKey(project, phase);

        return phaseExecutionCache.computeIfAbsent(cacheKey, key -> {
            try {
                if (lifecycleExecutor != null && session != null) {
                    if (verbose) {
                        log.debug("Computing execution plan for " + project.getArtifactId() + " phase: " + phase);

