    /**
     * Container for analyzed execution plan data for a single project with lazy loading
     */
    public static class ProjectExecutionAnalysis {
        private final MavenProject project;
        private final Map<String, String> goalToPhaseMap = new ConcurrentHashMap<>();
        private final Map<String, List<String>> phaseToGoalsCache = new ConcurrentHashMap<>();
        private final Map<String, ExecutionInfo> goalToExecutionInfo = new ConcurrentHashMap<>();

        public ProjectExecutionAnalysis(MavenProject project) {
            this.project = project;
        }

        /**
         * Get phase for a goal, computing if necessary
         */
        public String getPhaseForGoal(String goal) {
            return goalToPhaseMap.computeIfAbsent(goal, this::computePhaseForGoal);
        }

        /**
         * Get goals for a phase, computing if necessary
         */
        public List<String> getGoalsForPhase(String phase, ExecutionPlanAnalysisService service) {
            return phaseToGoalsCache.computeIfAbsent(phase, p -> computeGoalsForPhase(p, service));
        }

        /**
         * Compute phase for a goal by checking project plugin configuration
         */
        private String computePhaseForGoal(String goal) {
            if (project.getBuildPlugins() == null) {
                return null;
            }

            String targetGoal = extractGoalFromTargetName(goal);

            for (org.apache.maven.model.Plugin plugin : project.getBuildPlugins()) {
                if (plugin.getExecutions() != null) {
                    for (org.apache.maven.model.PluginExecution execution : plugin.getExecutions()) {
                        if (execution.getGoals() != null && execution.getGoals().contains(targetGoal)) {
                            return execution.getPhase();
                        }
                    }
                }
            }
            return null;
        }

        /**
         * Compute goals for a phase using execution plan analysis
         */
        private List<String> computeGoalsForPhase(String phase, ExecutionPlanAnalysisService service) {
            List<String> goals = new ArrayList<>();

            MavenExecutionPlan executionPlan = service.getExecutionPlan(project, phase);
            if (executionPlan != null) {
                for (MojoExecution mojoExecution : executionPlan.getMojoExecutions()) {
                    String goal = mojoExecution.getGoal();
                    String execPhase = mojoExecution.getLifecyclePhase();
                    String pluginArtifactId = mojoExecution.getPlugin().getArtifactId();

                    if (goal != null && phase.equals(execPhase)) {
                        String targetName = getTargetName(pluginArtifactId, goal);
                        goals.add(targetName);

                        // Cache goal-to-phase mapping
                        goalToPhaseMap.put(goal, execPhase);
                        goalToPhaseMap.put(targetName, execPhase);

                        // Cache execution info
                        ExecutionInfo execInfo = new ExecutionInfo(
                            goal, execPhase, pluginArtifactId,
                            mojoExecution.getExecutionId(),
                            mojoExecution.getPlugin().getGroupId() + ":" + pluginArtifactId
                        );
                        goalToExecutionInfo.put(goal, execInfo);
                        goalToExecutionInfo.put(targetName, execInfo);
                    }
                }
            }

            return goals;
        }

        public ExecutionInfo getExecutionInfo(String goal) {
            return goalToExecutionInfo.get(goal);
        }

        public Set<String> getAllPhases() {
            return new LinkedHashSet<>(phaseToGoalsCache.keySet());
        }

        public Set<String> getAllGoals() {
            return new LinkedHashSet<>(goalToPhaseMap.keySet());
        }

        /**
         * Get phase to goals mapping (legacy method for tests)
         */
        public Map<String, List<String>> getPhaseToGoalsMap() {
            return new HashMap<>(phaseToGoalsCache);
        }
    }

    /**
     * Detailed information about a mojo execution
     */
    public static class ExecutionInfo {
        private final String goal;
        private final String phase;
        private final String pluginArtifactId;
        private final String executionId;
        private final String pluginKey;

        public ExecutionInfo(String goal, String phase, String pluginArtifactId, String executionId, String pluginKey) {
            this.goal = goal;
            this.phase = phase;
            this.pluginArtifactId = pluginArtifactId;
            this.executionId = executionId;
            this.pluginKey = pluginKey;
        }

        public String getGoal() { return goal; }
        public String getPhase() { return phase; }
        public String getPluginArtifactId() { return pluginArtifactId; }
        public String getExecutionId() { return executionId; }
        public String getPluginKey() { return pluginKey; }
    }

    public List<String> getGoalOutputs(String goal, String projectRootToken, MavenProject project) {
        // Simplified implementation without hardcoded patterns
        return new ArrayList<>();
    }
}

