        }
        return artifactId.replace("-maven-plugin", "").replace("-plugin", "");
    }

    /**
     * Get common goals for well-known plugins
     */
    public static List<String> getCommonGoalsForPlugin(String artifactId) {
        List<String> commonGoals = new ArrayList<>();

        if (artifactId == null) {
