                    String pluginArtifactId = mojoExecution.getPlugin().getArtifactId();

                    if (goal != null && phase.equals(execPhase)) {
                        String targetName = getTargetName(pluginArtifactId, goal);
                        goals.add(targetName);

                        // Cache goal-to-phase mapping
                        goalToPhaseMap.put(goal, execPhase);
                        goalToPhaseMap.put(targetName, execPhase);

                        // Cache execution info
                        ExecutionInfo execInfo = new ExecutionInfo(
                            goal, execPhase, pluginArtifactId,
