        this.phaseToLifecycleMap = computePhaseToLifecycleMap();

        if (verbose) {
            log.info("Initialized execution plan analysis with " + allLifecyclePhases.size() + " lifecycle phases");
        }
    }

    /**
     * Get the LifecycleExecutor for use by other services
     */
    public LifecycleExecutor getLifecycleExecutor() {
        return lifecycleExecutor;
    }

    /**
     * Get the DefaultLifecycles for use by other services
     */
    public DefaultLifecycles getDefaultLifecycles() {
        return defaultLifecycles;
    }

    /**
     * Get or compute execution analysis for a project (lazy-loaded)
     */
    public ProjectExecutionAnalysis getAnalysis(MavenProject project) {
        String projectKey = MavenUtils.formatProjectKey(project);
        ProjectExecutionAnalysis existing = projectCache.get(projectKey);
        if (existing != null) {
            return existing;
        }

        // Create new analysis outside of computeIfAbsent to avoid deadlocks
        ProjectExecutionAnalysis newAnalysis = new ProjectExecutionAnalysis(project);
        ProjectExecutionAnalysis cached = projectCache.putIfAbsent(projectKey, newAnalysis);
        return cached != null ? cached : newAnalysis;
    }

    /**
     * Get execution plan for a specific phase, using smart caching
     */
    public MavenExecutionPlan getExecutionPlan(MavenProject project, String phase) {
        if (phase == null || phase.isEmpty()) {
            return null;
        }

        // Track requested phases for optimization insights
        requestedPhases.add(phase);

        // Create cache key based on project configuration that affects execution plans
        String cacheKey = createExecutionPlanCacheKey(project, phase);

        MavenExecutionPlan existing = phaseExecutionCache.get(cacheKey);
        if (existing != null) {
            return existing;
        }

        // Compute execution plan outside of computeIfAbsent to avoid deadlocks
        MavenExecutionPlan newPlan = null;
        try {
            if (lifecycleExecutor != null && session != null) {
                if (verbose) {
                    log.debug("Computing execution plan for " + project.getArtifactId() + " phase: " + phase);
                }
                newPlan = lifecycleExecutor.calculateExecutionPlan(session, phase);
            }
        } catch (Exception e) {
            if (verbose) {
                log.warn("Could not calculate execution plan for " + project.getArtifactId() + " phase " + phase + ": " + e.getMessage());
            }
        }

        // Cache the result (even if null) and return
        MavenExecutionPlan cached = phaseExecutionCache.putIfAbsent(cacheKey, newPlan);
        return cached != null ? cached : newPlan;
    }

    /**
     * Create a cache key for execution plans based on project characteristics
     */
    private String createExecutionPlanCacheKey(MavenProject project, String phase) {
        // Use project packaging and key plugins to determine cache key
        // Projects with similar configurations can share execution plans
        String packaging = project.getPackaging() != null ? project.getPackaging() : "jar";

        // Get key plugin artifacts that affect execution plans
        Set<String> keyPlugins = new LinkedHashSet<>();
        if (project.getBuildPlugins() != null) {
            for (org.apache.maven.model.Plugin plugin : project.getBuildPlugins()) {
                String artifactId = plugin.getArtifactId();
                // Only include plugins that significantly affect execution plans
                if (artifactId != null && (
                    artifactId.contains("quarkus") ||
                    artifactId.contains("spring-boot") ||
                    artifactId.contains("compiler") ||
                    artifactId.contains("surefire") ||
                    artifactId.contains("failsafe")
                )) {
                    keyPlugins.add(artifactId);
                }
            }
        }

        return phase + ":" + packaging + ":" + String.join(",", keyPlugins);
    }

    /**
     * Find the phase for a specific goal
     */
    public String findPhaseForGoal(MavenProject project, String goal) {
        if (project == null || goal == null || goal.isEmpty()) {
            return null;
        }
        ProjectExecutionAnalysis analysis = getAnalysis(project);
        return analysis.getPhaseForGoal(goal);
    }


    /**
     * Get all goals for a specific phase (lazy-loaded)
     */
    public List<String> getGoalsForPhase(MavenProject project, String phase) {
        if (project == null || phase == null) {
            return new ArrayList<>();
        }

        ProjectExecutionAnalysis analysis = getAnalysis(project);
        return analysis.getGoalsForPhase(phase, this);
    }

    /**
     * Get all lifecycle phases from all lifecycles (default, clean, site)
     */
    public Set<String> getAllLifecyclePhases() {
        return new LinkedHashSet<>(allLifecyclePhases);
