import { describe, it, expect, beforeAll } from 'vitest';
import { join } from 'path';
import { createNodesV2, createDependencies } from './maven-plugin';
import { CreateNodesContextV2, CreateDependenciesContext } from '@nx/devkit';

describe('io.quarkus:quarkus-core Project Analysis', () => {
  const workspaceRoot = __dirname;
  let quarkusCoreProject: any;
  let projectDependencies: any[];
  let allProjects: Map<string, any>;

  beforeAll(async () => {
    console.log('Analyzing io.quarkus:quarkus-core project...');

    const nodesContext: CreateNodesContextV2 = {
      nxJsonConfiguration: {},
      workspaceRoot,
      configFiles: ['pom.xml']
    };

    const depsContext: CreateDependenciesContext = {
      nxJsonConfiguration: {},
      workspaceRoot,
      externalNodes: {},
      projects: {},
      fileMap: { nonProjectFiles: [], projectFileMap: {} },
      filesToProcess: { nonProjectFiles: [], projectFileMap: {} }
    };

    try {
      // Generate nodes
      const nodesResults = await createNodesV2[1](['pom.xml'], {}, nodesContext);

      // Build project map
      allProjects = new Map();
      nodesResults.forEach(result => {
        if (result.nodes) {
          Object.entries(result.nodes).forEach(([key, node]) => {
            allProjects.set(key, node);
          });
        }
      });

      // Find the quarkus-core project
      quarkusCoreProject = allProjects.get('io.quarkus:quarkus-core');

      // Generate dependencies
      projectDependencies = await createDependencies({}, depsContext);

      console.log(`Found ${allProjects.size} projects, quarkus-core exists: ${!!quarkusCoreProject}`);
    } catch (error) {
      console.warn('Analysis failed:', error.message);
    }
  }, 180000); // 3 minute timeout

  describe('Project Basic Properties', () => {
    it('should have correct basic project metadata', () => {
      expect(quarkusCoreProject).toBeDefined();
      expect(quarkusCoreProject.name).toBe('io.quarkus:quarkus-core');
      expect(quarkusCoreProject.root).toBe('core/runtime');
      expect(quarkusCoreProject.projectType).toBe('library');
    });

    it('should have correct Maven metadata', () => {
      expect(quarkusCoreProject?.metadata?.groupId).toBe('io.quarkus');
      expect(quarkusCoreProject?.metadata?.artifactId).toBe('quarkus-core');
      expect(quarkusCoreProject?.metadata?.version).toBe('999-SNAPSHOT');
      expect(quarkusCoreProject?.metadata?.packaging).toBe('jar');
    });

    it('should have proper source and target structure', () => {
      expect(quarkusCoreProject?.root).toBe('core/runtime');
      expect(quarkusCoreProject?.sourceRoot).toBeNull(); // As shown in the actual data
    });
  });

  describe('Maven Lifecycle Targets', () => {
    it('should have all standard Maven lifecycle phase targets', () => {
      const expectedPhases = [
        'clean', 'validate', 'compile', 'test', 
        'package', 'verify', 'install', 'deploy', 'site'
      ];

      expectedPhases.forEach(phase => {
        expect(quarkusCoreProject?.targets?.[phase]).toBeDefined();
        expect(quarkusCoreProject.targets[phase]?.metadata?.type).toBe('phase');
        expect(quarkusCoreProject.targets[phase]?.metadata?.phase).toBe(phase);
      });
    });

    it('should have correct Maven plugin goal targets', () => {
      const expectedGoals = [
        'maven-enforcer:enforce',
        'buildnumber:create', 
        'maven-compiler:compile',
        'maven-compiler:testCompile',
        'maven-surefire:test',
        'maven-jar:jar',
        'maven-clean:clean',
        'maven-install:install',
        'maven-deploy:deploy'
      ];

      expectedGoals.forEach(goal => {
        expect(quarkusCoreProject?.targets?.[goal]).toBeDefined();
        expect(quarkusCoreProject.targets[goal]?.metadata?.type).toBe('goal');
      });
    });

    it('should have Quarkus-specific targets', () => {
      const quarkusTargets = [
        'quarkus-extension:dev',
        'quarkus-extension:build',
        'quarkus-extension:extension-descriptor'
      ];

      quarkusTargets.forEach(target => {
        expect(quarkusCoreProject?.targets?.[target]).toBeDefined();
        expect(quarkusCoreProject.targets[target]?.metadata?.plugin).toContain('quarkus-extension');
      });
    });
  });

  describe('Target Dependencies and Execution Order', () => {
    it('should have proper lifecycle phase dependencies', () => {
      // Test that lifecycle phases depend on previous phases using ^ syntax
      expect(quarkusCoreProject?.targets?.validate?.dependsOn).toContain('^validate');
      expect(quarkusCoreProject?.targets?.compile?.dependsOn).toContain('^compile');
      expect(quarkusCoreProject?.targets?.test?.dependsOn).toContain('^test');
      expect(quarkusCoreProject?.targets?.package?.dependsOn).toContain('^package');
    });

    it('should have correct goal-to-phase dependencies', () => {
      // Maven enforcer should depend on validate phase
      expect(quarkusCoreProject?.targets?.['maven-enforcer:enforce']?.dependsOn).toContain('^validate');

      // Compiler should depend on compile phase  
      expect(quarkusCoreProject?.targets?.['maven-compiler:compile']?.dependsOn).toContain('^compile');

      // Test compilation should depend on test-compile phase
      expect(quarkusCoreProject?.targets?.['maven-compiler:testCompile']?.dependsOn).toContain('^test-compile');

      // Tests should depend on test phase
      expect(quarkusCoreProject?.targets?.['maven-surefire:test']?.dependsOn).toContain('^test');
    });

    it('should have Quarkus extension descriptor dependencies', () => {
      const descriptorTarget = quarkusCoreProject?.targets?.['quarkus-extension:extension-descriptor'];
      expect(descriptorTarget?.dependsOn).toContain('^process-resources');
      expect(descriptorTarget?.metadata?.phase).toBe('process-resources');
    });
  });

  describe('Target Inputs and Outputs', () => {
    it('should have correct inputs for compilation targets', () => {
      const compileTarget = quarkusCoreProject?.targets?.['maven-compiler:compile'];
      expect(compileTarget?.inputs).toContain('{projectRoot}/pom.xml');
      expect(compileTarget?.inputs).toContain('{projectRoot}/src/**/*');
    });

    it('should have correct outputs for build targets', () => {
      const jarTarget = quarkusCoreProject?.targets?.['maven-jar:jar'];
      expect(jarTarget?.outputs).toContain('{projectRoot}/target/**/*');

      const compileTarget = quarkusCoreProject?.targets?.['maven-compiler:compile'];
      expect(compileTarget?.outputs).toContain('{projectRoot}/target/**/*');
    });

    it('should have test-specific outputs', () => {
      const testCompileTarget = quarkusCoreProject?.targets?.['maven-compiler:testCompile'];
      expect(testCompileTarget?.outputs).toContain('{projectRoot}/target/surefire-reports/**/*');
      expect(testCompileTarget?.outputs).toContain('{projectRoot}/target/failsafe-reports/**/*');
    });
  });

  describe('Target Executor Configuration', () => {
    it('should use nx:run-commands executor for Maven goals', () => {
      const compileTarget = quarkusCoreProject?.targets?.['maven-compiler:compile'];
      expect(compileTarget?.executor).toBe('nx:run-commands');
      expect(compileTarget?.options?.command).toContain('mvn');
      expect(compileTarget?.options?.cwd).toBe('core/runtime');
    });

    it('should use nx:noop executor for lifecycle phases', () => {
      const phases = ['clean', 'validate', 'compile', 'test', 'package'];
      phases.forEach(phase => {
        expect(quarkusCoreProject?.targets?.[phase]?.executor).toBe('nx:noop');
      });
    });

    it('should have parallelism enabled', () => {
      Object.values(quarkusCoreProject?.targets || {}).forEach((target: any) => {
        expect(target?.parallelism).toBe(true);
      });
    });
  });

  describe('Plugin and Technology Metadata', () => {
    it('should have correct plugin information for Maven goals', () => {
      const enforceTarget = quarkusCoreProject?.targets?.['maven-enforcer:enforce'];
      expect(enforceTarget?.metadata?.plugin).toBe('org.apache.maven.plugins:maven-enforcer-plugin');
      expect(enforceTarget?.metadata?.goal).toBe('enforce');
      expect(enforceTarget?.metadata?.technologies).toContain('maven');
    });

    it('should have Quarkus plugin metadata', () => {
      const devTarget = quarkusCoreProject?.targets?.['quarkus-extension:dev'];
      expect(devTarget?.metadata?.plugin).toBe('io.quarkus:quarkus-extension-maven-plugin');
      expect(devTarget?.metadata?.goal).toBe('dev');
      expect(devTarget?.metadata?.description).toBe('Start development mode');
    });

    it('should have proper target groupings', () => {
      const targetGroups = quarkusCoreProject?.metadata?.targetGroups;
      expect(targetGroups?.validate).toContain('maven-enforcer:enforce');
      expect(targetGroups?.package).toContain('maven-jar:jar');
      expect(targetGroups?.clean).toContain('maven-clean:clean');
    });
  });

  describe('Project Dependencies Analysis', () => {
    it('should have dependencies from/to quarkus-core', () => {
      const coreDependencies = projectDependencies.filter(dep => 
        dep.source === 'io.quarkus:quarkus-core' || dep.target === 'io.quarkus:quarkus-core'
      );

      console.log(`Found ${coreDependencies.length} dependencies involving quarkus-core:`);
      coreDependencies.forEach(dep => {
        console.log(`  ${dep.source} -> ${dep.target} (${dep.type})`);
      });
    });

    it('should be depended on by other Quarkus projects', () => {
      // quarkus-core is a fundamental library, other projects should depend on it
      const dependents = projectDependencies.filter(dep => 
        dep.target === 'io.quarkus:quarkus-core'
      );

      console.log(`Projects that depend on quarkus-core: ${dependents.length}`);
      dependents.forEach(dep => {
        console.log(`  ${dep.source} depends on quarkus-core`);
      });
    });

    it('should have target-level dependencies', () => {
      const targetDeps = projectDependencies.filter(dep => 
        (dep.source.startsWith('io.quarkus:quarkus-core:') || 
         dep.target.startsWith('io.quarkus:quarkus-core:')) &&
        dep.source.includes(':') && dep.target.includes(':')
      );

      console.log(`Target-level dependencies for quarkus-core: ${targetDeps.length}`);
      targetDeps.forEach(dep => {
        console.log(`  ${dep.source} -> ${dep.target}`);
      });
    });
  });

  describe('Cross-Module Dependency Patterns', () => {
    it('should follow proper cross-module dependency patterns', () => {
      // Check that lifecycle phases properly use ^ syntax for cross-module dependencies
      const phases = ['validate', 'compile', 'test', 'package', 'install', 'deploy'];
      phases.forEach(phase => {
        const target = quarkusCoreProject?.targets?.[phase];
        const crossModuleDeps = target?.dependsOn?.filter((dep: string) => dep.startsWith('^'));
        expect(crossModuleDeps?.length).toBeGreaterThan(0);
      });
    });

    it('should have consistent dependency structure with other core projects', () => {
      // Compare with quarkus-core-deployment if it exists
      const coreDeployment = allProjects.get('io.quarkus:quarkus-core-deployment');
      if (coreDeployment) {
        // Both should have similar target structure
        const coreTargets = Object.keys(quarkusCoreProject?.targets || {});
        const deploymentTargets = Object.keys(coreDeployment?.targets || {});

        // Both should have basic Maven lifecycle phases
        const basicPhases = ['clean', 'compile', 'test', 'package'];
        basicPhases.forEach(phase => {
          expect(coreTargets).toContain(phase);
          expect(deploymentTargets).toContain(phase);
        });
      }
    });
  });

  describe('Build Configuration Validation', () => {
    it('should have proper working directory configuration', () => {
      Object.entries(quarkusCoreProject?.targets || {}).forEach(([name, target]: [string, any]) => {
        if (target?.options?.cwd) {
          expect(target.options.cwd).toBe('core/runtime');
        }
      });
    });

    it('should have meaningful target descriptions', () => {
      const targetsWithDescriptions = Object.entries(quarkusCoreProject?.targets || {})
        .filter(([_, target]: [string, any]) => target?.metadata?.description);

      expect(targetsWithDescriptions.length).toBeGreaterThan(5);

      // Check specific descriptions
      expect(quarkusCoreProject?.targets?.['maven-compiler:compile']?.metadata?.description)
        .toBe('Compile main sources');
      expect(quarkusCoreProject?.targets?.['maven-surefire:test']?.metadata?.description)
        .toBe('Run tests');
    });

    it('should have consistent configuration across similar targets', () => {
      // All Maven plugin goals should have similar structure
      const mavenGoals = Object.entries(quarkusCoreProject?.targets || {})
        .filter(([name, _]) => name.includes(':') && !name.startsWith('quarkus-extension'));

      mavenGoals.forEach(([name, target]) => {
        expect(target.executor).toBe('nx:run-commands');
        expect(target.parallelism).toBe(true);
        expect(target.metadata?.technologies).toContain('maven');
        expect(target.options?.cwd).toBe('core/runtime');
      });
    });
  });

  describe('Performance and Cache Configuration', () => {
    it('should have appropriate input/output configuration for caching', () => {
      // Compilation targets should have both inputs and outputs for caching
      const compileTarget = quarkusCoreProject?.targets?.['maven-compiler:compile'];
      expect(compileTarget?.inputs?.length).toBeGreaterThan(0);
      expect(compileTarget?.outputs?.length).toBeGreaterThan(0);

      // Test targets should have inputs and outputs  
      const testTarget = quarkusCoreProject?.targets?.['maven-surefire:test'];
      expect(testTarget?.inputs?.length).toBeGreaterThan(0);
      expect(testTarget?.outputs?.length).toBeGreaterThan(0);
    });

    it('should have parallelism enabled for all targets', () => {
      Object.entries(quarkusCoreProject?.targets || {}).forEach(([name, target]: [string, any]) => {
        expect(target?.parallelism).toBe(true);
      });
    });

    it('should have reasonable dependency chains for build performance', () => {
      // Check that dependency chains are not overly complex
      Object.entries(quarkusCoreProject?.targets || {}).forEach(([name, target]: [string, any]) => {
        const dependsOn = target?.dependsOn || [];
        // No target should depend on more than 10 other targets (reasonable limit)
        expect(dependsOn.length).toBeLessThan(10);
      });
    });
  });
});

