      const dependents = projectDependencies.filter(dep => 
        dep.target === 'io.quarkus:quarkus-core'
      );

      console.log(`Projects that depend on quarkus-core: ${dependents.length}`);
      dependents.forEach(dep => {
        console.log(`  ${dep.source} depends on quarkus-core`);
      });
    });

    it('should have target-level dependencies', () => {
      const targetDeps = projectDependencies.filter(dep => 
        (dep.source.startsWith('io.quarkus:quarkus-core:') || 
