import { describe, it, expect, beforeAll } from 'vitest';
import { createNodesV2 } from './maven-plugin';
import { CreateNodesContextV2 } from '@nx/devkit';

describe('Maven Lifecycle Phases Analysis', () => {
  const workspaceRoot = __dirname;
  let allProjects: Map<string, any>;
  let quarkusCoreProject: any;

  beforeAll(async () => {
    console.log('Analyzing Maven lifecycle phases...');

    const nodesContext: CreateNodesContextV2 = {
      nxJsonConfiguration: {},
      workspaceRoot,
      configFiles: ['pom.xml']
    };

    try {
      const nodesResults = await createNodesV2[1](['pom.xml'], {}, nodesContext);

      allProjects = new Map();
      nodesResults.forEach(result => {
        if (result.nodes) {
          Object.entries(result.nodes).forEach(([key, node]) => {
            allProjects.set(key, node);
          });
        }
      });

      quarkusCoreProject = allProjects.get('io.quarkus:quarkus-core');
      console.log(`Found ${allProjects.size} projects, quarkus-core exists: ${!!quarkusCoreProject}`);
    } catch (error) {
      console.warn('Maven analysis failed:', error.message);
    }
  }, 180000);

  describe('Standard Maven Lifecycle Phases', () => {
    const standardPhases = [
      'validate', 'initialize', 'generate-sources', 'process-sources',
      'generate-resources', 'process-resources', 'compile', 'process-classes',
      'generate-test-sources', 'process-test-sources', 'generate-test-resources',
      'process-test-resources', 'test-compile', 'process-test-classes',
      'test', 'prepare-package', 'package', 'pre-integration-test',
      'integration-test', 'post-integration-test', 'verify', 'install',
      'deploy'
    ];

    const cleanPhases = ['pre-clean', 'clean', 'post-clean'];
    const sitePhases = ['pre-site', 'site', 'post-site', 'site-deploy'];

    it('should identify lifecycle phases correctly', () => {
      if (!quarkusCoreProject?.targets) {
        console.log('No targets found - skipping phase identification test');
        return;
      }

      const phaseTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => target?.metadata?.type === 'phase');

      console.log('Found lifecycle phases:');
      phaseTargets.forEach(([name, target]) => {
        console.log(`  ${name}: phase=${target.metadata?.phase}`);
      });

      expect(phaseTargets.length).toBeGreaterThan(0);
    });

    it('should have nx:noop executor for all lifecycle phases', () => {
      if (!quarkusCoreProject?.targets) return;

      const phaseTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => target?.metadata?.type === 'phase');

      phaseTargets.forEach(([name, target]) => {
        expect(target?.executor).toBe('nx:noop');
        console.log(` ${name} uses nx:noop executor`);
      });
    });

    it('should have proper phase dependencies using ^ syntax', () => {
      if (!quarkusCoreProject?.targets) return;

      const phaseTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => target?.metadata?.type === 'phase');

      console.log('Phase dependency analysis:');
      phaseTargets.forEach(([name, target]) => {
        const dependsOn = target?.dependsOn || [];
        const crossModuleDeps = dependsOn.filter((dep: string) => dep.startsWith('^'));

        console.log(`  ${name}: dependsOn=[${dependsOn.join(', ')}]`);
        console.log(`    Cross-module deps: [${crossModuleDeps.join(', ')}]`);

        // All phases should have at least one cross-module dependency
        if (dependsOn.length > 0) {
          expect(crossModuleDeps.length).toBeGreaterThan(0);
        }
      });
    });

    it('should distinguish between default lifecycle and clean lifecycle phases', () => {
      if (!quarkusCoreProject?.targets) return;

      const allPhases = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => target?.metadata?.type === 'phase')
        .map(([name, _]) => name);

      const foundCleanPhases = allPhases.filter(phase => cleanPhases.includes(phase));
      const foundDefaultPhases = allPhases.filter(phase => standardPhases.includes(phase));

      console.log('Clean lifecycle phases found:', foundCleanPhases);
      console.log('Default lifecycle phases found:', foundDefaultPhases);

      // Should have at least clean phase
      expect(foundCleanPhases).toContain('clean');

      // Should have common default phases
      const commonPhases = ['validate', 'compile', 'test', 'package', 'install', 'deploy'];
      commonPhases.forEach(phase => {
        if (allPhases.includes(phase)) {
          expect(foundDefaultPhases).toContain(phase);
        }
      });
    });

    it('should have site lifecycle phases if site plugin is configured', () => {
      if (!quarkusCoreProject?.targets) return;

      const allPhases = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => target?.metadata?.type === 'phase')
        .map(([name, _]) => name);

      const foundSitePhases = allPhases.filter(phase => sitePhases.includes(phase));

      console.log('Site lifecycle phases found:', foundSitePhases);

      if (foundSitePhases.length > 0) {
        expect(foundSitePhases).toContain('site');
      }
    });
  });

  describe('Phase Metadata Analysis', () => {
    it('should have correct metadata structure for phases', () => {
      if (!quarkusCoreProject?.targets) return;

      const phaseTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => target?.metadata?.type === 'phase');

      phaseTargets.forEach(([name, target]) => {
        expect(target?.metadata?.type).toBe('phase');
        expect(target?.metadata?.phase).toBe(name);
        expect(target?.metadata?.plugin).toBe('org.apache.maven:maven-core');
        expect(target?.metadata?.goal).toBeNull();
        expect(target?.metadata?.technologies).toContain('maven');

        console.log(` ${name} has correct phase metadata`);
      });
    });

    it('should have meaningful descriptions for phases', () => {
      if (!quarkusCoreProject?.targets) return;

      const phaseTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => target?.metadata?.type === 'phase');

      phaseTargets.forEach(([name, target]) => {
        expect(target?.metadata?.description).toBeDefined();
        expect(target?.metadata?.description).toContain('Maven lifecycle phase');

        console.log(`  ${name}: "${target?.metadata?.description}"`);
      });
    });

    it('should have empty inputs and outputs for phases', () => {
      if (!quarkusCoreProject?.targets) return;

      const phaseTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => target?.metadata?.type === 'phase');

      phaseTargets.forEach(([name, target]) => {
        // Phases are orchestrators, they don't have direct inputs/outputs
        expect(target?.inputs).toEqual([]);
        expect(target?.outputs).toEqual([]);

        console.log(` ${name} has empty inputs/outputs (orchestrator pattern)`);
      });
    });

    it('should have parallelism enabled for phases', () => {
      if (!quarkusCoreProject?.targets) return;

      const phaseTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => target?.metadata?.type === 'phase');

      phaseTargets.forEach(([name, target]) => {
        expect(target?.parallelism).toBe(true);
        console.log(` ${name} has parallelism enabled`);
      });
    });
  });

  describe('Phase Dependency Patterns', () => {
    it('should follow proper Maven lifecycle ordering', () => {
      if (!quarkusCoreProject?.targets) return;

      const phaseOrder = ['validate', 'compile', 'test', 'package', 'verify', 'install', 'deploy'];

      console.log('Checking phase dependency ordering:');
      phaseOrder.forEach((phase, index) => {
        const target = quarkusCoreProject.targets[phase];
        if (target) {
          const dependsOn = target?.dependsOn || [];
          const crossModuleDep = `^${phase}`;

          console.log(`  ${phase}: dependsOn=[${dependsOn.join(', ')}]`);

          // Each phase should depend on its cross-module equivalent
          expect(dependsOn).toContain(crossModuleDep);

          // Later phases may also depend on goals bound to the phase
          if (index > 0) {
            // Should have dependencies beyond just the cross-module one
            expect(dependsOn.length).toBeGreaterThanOrEqual(1);
          }
        }
      });
    });

    it('should aggregate goals bound to each phase', () => {
      if (!quarkusCoreProject?.targets) return;

      const phases = ['validate', 'compile', 'test', 'package'];

      console.log('Phase to goal aggregation:');
      phases.forEach(phase => {
        const target = quarkusCoreProject.targets[phase];
        if (target) {
          const dependsOn = target?.dependsOn || [];
          const goalDeps = dependsOn.filter((dep: string) => 
            dep.includes(':') && !dep.startsWith('^')
          );

          console.log(`  ${phase} aggregates goals: [${goalDeps.join(', ')}]`);

          // Most phases should aggregate some goals
          if (['compile', 'test', 'package'].includes(phase)) {
            expect(goalDeps.length).toBeGreaterThan(0);
          }
        }
      });
    });

    it('should have consistent cross-module dependency naming', () => {
      if (!quarkusCoreProject?.targets) return;

      const phaseTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => target?.metadata?.type === 'phase');

      phaseTargets.forEach(([name, target]) => {
        const dependsOn = target?.dependsOn || [];
        const crossModuleDeps = dependsOn.filter((dep: string) => dep.startsWith('^'));

        // Should have exactly one cross-module dependency with same name
        const expectedCrossModuleDep = `^${name}`;
        expect(crossModuleDeps).toContain(expectedCrossModuleDep);

        console.log(` ${name} correctly depends on ${expectedCrossModuleDep}`);
      });
    });
  });

  describe('Cross-Project Phase Analysis', () => {
    it('should compare phase structure across multiple projects', () => {
      if (allProjects.size === 0) return;

      const projectsWithPhases = new Map();

      allProjects.forEach((project, projectName) => {
        if (project?.targets) {
          const phases = Object.entries(project.targets)
            .filter(([_, target]: [string, any]) => target?.metadata?.type === 'phase')
            .map(([name, _]) => name);

          if (phases.length > 0) {
            projectsWithPhases.set(projectName, phases);
          }
        }
      });

      console.log(`Projects with phases: ${projectsWithPhases.size}`);

      // Compare phase consistency across projects
      const allPhases = new Set();
      projectsWithPhases.forEach((phases, projectName) => {
        phases.forEach((phase: string) => allPhases.add(phase));
        console.log(`  ${projectName}: [${phases.join(', ')}]`);
      });

      console.log(`Unique phases across all projects: [${Array.from(allPhases).join(', ')}]`);

      // Most projects should have common phases
      expect(allPhases.has('clean')).toBe(true);
      expect(allPhases.has('compile')).toBe(true);
      expect(allPhases.has('test')).toBe(true);
    });
  });
});

