      buildnumberGoals.forEach(([name, target]) => {
        console.log(`  ${name}: executionId=${target.metadata?.executionId}`);
      });

      if (buildnumberGoals.length > 0) {
        buildnumberGoals.forEach(([name, target]) => {
          expect(target?.metadata?.plugin).toContain('buildnumber-maven-plugin');
        });
      }
    });

    it('should identify enforcer plugin goals', () => {
      if (!quarkusCoreProject?.targets) return;

      const enforcerGoals = Object.entries(quarkusCoreProject.targets)
        .filter(([name, target]: [string, any]) => 
          name.startsWith('maven-enforcer:') && target?.metadata?.type === 'goal'
        );

      console.log('Maven Enforcer Goals:');
      enforcerGoals.forEach(([name, target]) => {
        console.log(`  ${name}: goal=${target.metadata?.goal}, executionId=${target.metadata?.executionId}`);
      });

      if (enforcerGoals.length > 0) {
        enforcerGoals.forEach(([name, target]) => {
          expect(target?.metadata?.plugin).toContain('maven-enforcer-plugin');
          expect(target?.metadata?.goal).toBe('enforce');
        });
      }
    });
  });

  describe('Goal Metadata Analysis', () => {
    it('should have correct metadata structure for goals', () => {
      if (!quarkusCoreProject?.targets) return;

      const goalTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => target?.metadata?.type === 'goal');

      goalTargets.forEach(([name, target]) => {
        expect(target?.metadata?.type).toBe('goal');
