      if (!quarkusCoreProject?.targets) return;

      const goalTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => target?.metadata?.type === 'goal');

      goalTargets.forEach(([name, target]) => {
        expect(target?.executor).toBe('nx:run-commands');
        expect(target?.options?.command).toContain('mvn');
        expect(target?.options?.cwd).toBe('core/runtime');

        console.log(` ${name} uses nx:run-commands executor with mvn command`);
      });
    });

    it('should have meaningful commands for goals', () => {
      if (!quarkusCoreProject?.targets) return;

      const goalTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => target?.metadata?.type === 'goal');

      goalTargets.forEach(([name, target]) => {
        const command = target?.options?.command;
        expect(command).toContain('mvn');
        expect(command).toContain(target?.metadata?.plugin);
        expect(command).toContain(target?.metadata?.goal);

        console.log(`  ${name}: "${command}"`);
      });
    });
  });

  describe('Goal Execution Phases', () => {
    it('should bind goals to correct lifecycle phases', () => {
      if (!quarkusCoreProject?.targets) return;

      const goalsByPhase = new Map();

      Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => target?.metadata?.type === 'goal')
        .forEach(([name, target]) => {
          const phase = target?.metadata?.phase;
          if (phase) {
            if (!goalsByPhase.has(phase)) {
              goalsByPhase.set(phase, []);
            }
            goalsByPhase.get(phase).push(name);
          }
        });

      console.log('Goals bound to lifecycle phases:');
      goalsByPhase.forEach((goals, phase) => {
        console.log(`  ${phase}: [${goals.join(', ')}]`);
      });

      // Verify expected phase bindings
      if (goalsByPhase.has('compile')) {
        expect(goalsByPhase.get('compile')).toContain('maven-compiler:compile');
      }

      if (goalsByPhase.has('test')) {
        const testGoals = goalsByPhase.get('test');
        const hasTestGoal = testGoals.some((goal: string) => goal.includes('test'));
        expect(hasTestGoal).toBe(true);
      }
    });

    it('should have unbound goals for development commands', () => {
      if (!quarkusCoreProject?.targets) return;

      const unboundGoals = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => 
          target?.metadata?.type === 'goal' && !target?.metadata?.phase
        );

      console.log('Unbound goals (development commands):');
      unboundGoals.forEach(([name, target]) => {
        console.log(`  ${name}: ${target?.metadata?.description || 'No description'}`);
      });

      // Development goals like quarkus:dev should be unbound
      const devGoals = unboundGoals.filter(([name]) => 
        name.includes(':dev') || name.includes(':run')
      );

      if (devGoals.length > 0) {
        console.log('Development goals found (correctly unbound)');
      }
    });
  });

  describe('Goal Dependencies and Inputs/Outputs', () => {
    it('should have proper cross-module dependencies for bound goals', () => {
      if (!quarkusCoreProject?.targets) return;

      const boundGoals = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => 
          target?.metadata?.type === 'goal' && target?.metadata?.phase
        );

      boundGoals.forEach(([name, target]) => {
        const dependsOn = target?.dependsOn || [];
        const phase = target?.metadata?.phase;
        const expectedCrossModuleDep = `^${phase}`;

        console.log(`  ${name} (${phase}): dependsOn=[${dependsOn.join(', ')}]`);

        // Bound goals should depend on their phase in other modules
        expect(dependsOn).toContain(expectedCrossModuleDep);
      });
    });

    it('should have appropriate inputs for compilation goals', () => {
      if (!quarkusCoreProject?.targets) return;

      const compilationGoals = Object.entries(quarkusCoreProject.targets)
        .filter(([name, target]: [string, any]) => 
          target?.metadata?.type === 'goal' && 
          (name.includes('compile') || name.includes('resources'))
        );

      compilationGoals.forEach(([name, target]) => {
        const inputs = target?.inputs || [];

        console.log(`  ${name} inputs: [${inputs.join(', ')}]`);

        // Should include pom.xml and source files
        expect(inputs).toContain('{projectRoot}/pom.xml');

        if (name.includes('compile') || name.includes('resources')) {
          const hasSourceInputs = inputs.some((input: string) => 
            input.includes('src/**/*')
          );
          expect(hasSourceInputs).toBe(true);
        }
      });
    });

    it('should have appropriate outputs for build goals', () => {
      if (!quarkusCoreProject?.targets) return;

      const buildGoals = Object.entries(quarkusCoreProject.targets)
        .filter(([name, target]: [string, any]) => 
          target?.metadata?.type === 'goal' && 
          (name.includes('compile') || name.includes('jar') || name.includes('build'))
        );

      buildGoals.forEach(([name, target]) => {
        const outputs = target?.outputs || [];

        console.log(`  ${name} outputs: [${outputs.join(', ')}]`);

        // Build goals should produce outputs in target directory
        if (outputs.length > 0) {
          const hasTargetOutput = outputs.some((output: string) => 
            output.includes('target')
          );
          expect(hasTargetOutput).toBe(true);
        }
      });
    });
  });

  describe('Goal Performance Configuration', () => {
    it('should have parallelism enabled for goals', () => {
      if (!quarkusCoreProject?.targets) return;

      const goalTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => target?.metadata?.type === 'goal');

      goalTargets.forEach(([name, target]) => {
        expect(target?.parallelism).toBe(true);
        console.log(` ${name} has parallelism enabled`);
      });
    });

    it('should have reasonable dependency counts for goals', () => {
      if (!quarkusCoreProject?.targets) return;

      const goalTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => target?.metadata?.type === 'goal');

      goalTargets.forEach(([name, target]) => {
        const dependsOn = target?.dependsOn || [];

        console.log(`  ${name}: ${dependsOn.length} dependencies`);

        // Goals shouldn't have too many direct dependencies
        expect(dependsOn.length).toBeLessThan(5);
      });
    });
  });

  describe('Cross-Project Goal Comparison', () => {
    it('should compare goal structure across multiple projects', () => {
      if (allProjects.size === 0) return;

      const goalsByPlugin = new Map();

      allProjects.forEach((project, projectName) => {
        if (project?.targets) {
          Object.entries(project.targets)
            .filter(([_, target]: [string, any]) => target?.metadata?.type === 'goal')
            .forEach(([name, target]) => {
              const plugin = target?.metadata?.plugin;
              if (plugin) {
                if (!goalsByPlugin.has(plugin)) {
                  goalsByPlugin.set(plugin, new Set());
                }
                goalsByPlugin.get(plugin).add(name);
              }
            });
        }
      });

      console.log('Goals by plugin across all projects:');
      goalsByPlugin.forEach((goals, plugin) => {
        console.log(`  ${plugin}: [${Array.from(goals).join(', ')}]`);
      });

      // Should have common Maven plugins
      expect(goalsByPlugin.has('org.apache.maven.plugins:maven-compiler-plugin')).toBe(true);
    });
  });
});

