      expect(targetsWithCrossModuleDeps.length).toBeGreaterThan(0);
    });

    it('should validate ^ syntax patterns for lifecycle phases', () => {
      if (!quarkusCoreProject?.targets) return;

      const lifecyclePhases = ['validate', 'compile', 'test', 'package', 'verify', 'install', 'deploy'];

      console.log('Lifecycle phase cross-module dependencies:');
      lifecyclePhases.forEach(phase => {
        const target = quarkusCoreProject.targets[phase];
        if (target) {
          const dependsOn = target?.dependsOn || [];
          const expectedCrossModuleDep = `^${phase}`;

          console.log(`  ${phase}: dependsOn=[${dependsOn.join(', ')}]`);

          // Each lifecycle phase should depend on the same phase in other modules
          expect(dependsOn).toContain(expectedCrossModuleDep);
        }
      });
    });

    it('should validate ^ syntax patterns for bound plugin goals', () => {
      if (!quarkusCoreProject?.targets) return;

      const boundGoals = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => 
          target?.metadata?.type === 'goal' && target?.metadata?.phase
        );

      console.log('Bound plugin goals cross-module dependencies:');
      boundGoals.forEach(([name, target]) => {
        const dependsOn = target?.dependsOn || [];
        const phase = target?.metadata?.phase;
        const expectedCrossModuleDep = `^${phase}`;

        console.log(`  ${name} (bound to ${phase}): dependsOn=[${dependsOn.join(', ')}]`);

        // Bound goals should depend on their lifecycle phase in other modules
        expect(dependsOn).toContain(expectedCrossModuleDep);
      });
    });

    it('should not use ^ syntax for same-module dependencies', () => {
      if (!quarkusCoreProject?.targets) return;

      Object.entries(quarkusCoreProject.targets).forEach(([name, target]) => {
        const dependsOn = target?.dependsOn || [];
        const localDeps = dependsOn.filter((dep: string) => !dep.startsWith('^'));

        // Local dependencies should be valid target names within the same project
        localDeps.forEach((dep: string) => {
          if (dep.includes(':')) {
            // If it's a target:goal format, it should refer to goals in same project
            console.log(`  ${name} has local goal dependency: ${dep}`);
          } else {
            // Should be a valid target name in the same project
            const targetExists = quarkusCoreProject.targets[dep];
            if (!targetExists) {
              console.warn(`  ${name} depends on non-existent local target: ${dep}`);
            }
          }
        });
      });
    });
  });

  describe('Project-Level Dependency Analysis', () => {
    it('should identify project-to-project dependencies', () => {
      const projectLevelDeps = projectDependencies.filter(dep => 
        !dep.source.includes(':') && !dep.target.includes(':')
      );

      console.log(`Found ${projectLevelDeps.length} project-level dependencies:`);
      projectLevelDeps.forEach((dep, index) => {
        console.log(`  ${index + 1}. ${dep.source} -> ${dep.target} (${dep.type})`);
      });

      // Look for dependencies involving quarkus-core
      const coreDeps = projectLevelDeps.filter(dep => 
        dep.source === 'io.quarkus:quarkus-core' || dep.target === 'io.quarkus:quarkus-core'
      );

      console.log(`Dependencies involving quarkus-core: ${coreDeps.length}`);
      coreDeps.forEach(dep => {
        console.log(`  ${dep.source} -> ${dep.target}`);
      });
    });

    it('should identify target-level dependencies', () => {
      const targetLevelDeps = projectDependencies.filter(dep => 
        dep.source.includes(':') && dep.target.includes(':')
      );

      console.log(`Found ${targetLevelDeps.length} target-level dependencies:`);
      targetLevelDeps.slice(0, 10).forEach((dep, index) => {
        console.log(`  ${index + 1}. ${dep.source} -> ${dep.target} (${dep.type})`);
      });

      // Look for target dependencies involving quarkus-core
      const coreTargetDeps = targetLevelDeps.filter(dep => 
        dep.source.startsWith('io.quarkus:quarkus-core:') || 
        dep.target.startsWith('io.quarkus:quarkus-core:')
      );

      console.log(`Target dependencies involving quarkus-core: ${coreTargetDeps.length}`);
      coreTargetDeps.forEach(dep => {
        console.log(`  ${dep.source} -> ${dep.target}`);
      });
    });

    it('should validate dependency graph structure', () => {
      // Build dependency graph
      const dependencyGraph = new Map();
      projectDependencies.forEach(dep => {
        if (!dependencyGraph.has(dep.source)) {
          dependencyGraph.set(dep.source, []);
        }
        dependencyGraph.get(dep.source).push(dep.target);
      });

      console.log('Dependency graph structure:');
      const sources = Array.from(dependencyGraph.keys()).slice(0, 5);
      sources.forEach(source => {
        const targets = dependencyGraph.get(source);
        console.log(`  ${source} -> [${targets.join(', ')}]`);
      });

      // Check for potential circular dependencies
      const checkCircular = (node: string, visited: Set<string>, recursionStack: Set<string>): boolean => {
        if (recursionStack.has(node)) return true;
        if (visited.has(node)) return false;

        visited.add(node);
        recursionStack.add(node);

        const neighbors = dependencyGraph.get(node) || [];
        for (const neighbor of neighbors) {
          if (checkCircular(neighbor, visited, recursionStack)) {
            return true;
          }
        }

        recursionStack.delete(node);
        return false;
      };

      const visited = new Set<string>();
      const hasCircularDeps = Array.from(dependencyGraph.keys()).some(node => 
        checkCircular(node, visited, new Set())
      );

      console.log(`Has circular dependencies: ${hasCircularDeps}`);
      expect(hasCircularDeps).toBe(false);
    });
  });

  describe('Cross-Module Execution Ordering', () => {
    it('should ensure proper execution order across modules', () => {
      if (!quarkusCoreProject?.targets) return;

      const lifecycleOrder = ['validate', 'initialize', 'compile', 'test', 'package', 'verify', 'install', 'deploy'];

      console.log('Cross-module execution order validation:');
      lifecycleOrder.forEach((phase, index) => {
        const target = quarkusCoreProject.targets[phase];
        if (target) {
          const dependsOn = target?.dependsOn || [];
          const crossModuleDeps = dependsOn.filter((dep: string) => dep.startsWith('^'));

          console.log(`  ${phase} (order ${index}): cross-module deps=[${crossModuleDeps.join(', ')}]`);

          // Should depend on the same phase in other modules
          expect(crossModuleDeps).toContain(`^${phase}`);

          // Should not depend on later phases
          const laterPhases = lifecycleOrder.slice(index + 1);
          laterPhases.forEach(laterPhase => {
            expect(crossModuleDeps).not.toContain(`^${laterPhase}`);
          });
        }
      });
    });

    it('should validate goal execution order within phases', () => {
      if (!quarkusCoreProject?.targets) return;

      const phaseTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => target?.metadata?.type === 'phase');

      console.log('Goal aggregation within phases:');
      phaseTargets.forEach(([phaseName, target]) => {
        const dependsOn = target?.dependsOn || [];
        const goalDeps = dependsOn.filter((dep: string) => 
          dep.includes(':') && !dep.startsWith('^')
        );

        if (goalDeps.length > 0) {
          console.log(`  ${phaseName} aggregates goals: [${goalDeps.join(', ')}]`);

          // Goals should be bound to this phase
          goalDeps.forEach((goalDep: string) => {
            const goalTarget = quarkusCoreProject.targets[goalDep];
            if (goalTarget?.metadata?.phase) {
              expect(goalTarget.metadata.phase).toBe(phaseName);
            }
          });
        }
      });
    });
  });

  describe('Cross-Project Dependency Patterns', () => {
    it('should identify common dependency patterns across projects', () => {
      if (allProjects.size === 0) return;

      const crossModuleUsage = new Map();

      allProjects.forEach((project, projectName) => {
        if (project?.targets) {
          Object.entries(project.targets).forEach(([targetName, target]) => {
            const dependsOn = target?.dependsOn || [];
            const crossModuleDeps = dependsOn.filter((dep: string) => dep.startsWith('^'));

            crossModuleDeps.forEach((dep: string) => {
              if (!crossModuleUsage.has(dep)) {
                crossModuleUsage.set(dep, []);
              }
              crossModuleUsage.get(dep).push(`${projectName}:${targetName}`);
            });
          });
        }
      });

      console.log('Most common cross-module dependencies:');
      const sortedUsage = Array.from(crossModuleUsage.entries())
        .sort(([,a], [,b]) => b.length - a.length)
        .slice(0, 10);

      sortedUsage.forEach(([dep, users]) => {
        console.log(`  ${dep}: used by ${users.length} targets`);
      });

      // Should have common patterns like ^compile, ^test, etc.
      expect(crossModuleUsage.has('^compile')).toBe(true);
      expect(crossModuleUsage.has('^test')).toBe(true);
    });

    it('should validate consistency of cross-module patterns', () => {
      if (allProjects.size === 0) return;

      const projectPatterns = new Map();

      allProjects.forEach((project, projectName) => {
        if (project?.targets) {
          const phases = Object.entries(project.targets)
            .filter(([_, target]: [string, any]) => target?.metadata?.type === 'phase')
            .map(([name, target]) => {
              const dependsOn = target?.dependsOn || [];
              const crossModuleDeps = dependsOn.filter((dep: string) => dep.startsWith('^'));
              return { phase: name, crossModuleDeps };
            });

          projectPatterns.set(projectName, phases);
        }
      });

      console.log('Cross-module dependency patterns by project:');

      // Compare patterns across projects
      const corePatterns = projectPatterns.get('io.quarkus:quarkus-core');
      if (corePatterns) {
        console.log('  quarkus-core patterns:');
        corePatterns.forEach(({ phase, crossModuleDeps }) => {
          console.log(`    ${phase}: [${crossModuleDeps.join(', ')}]`);
        });

        // Other projects should have similar patterns for common phases
        projectPatterns.forEach((patterns, projectName) => {
          if (projectName !== 'io.quarkus:quarkus-core' && patterns.length > 0) {
            const commonPhases = ['compile', 'test', 'package'];
            commonPhases.forEach(phase => {
              const corePattern = corePatterns.find(p => p.phase === phase);
              const projectPattern = patterns.find(p => p.phase === phase);

              if (corePattern && projectPattern) {
                // Should have similar cross-module dependency pattern
                expect(projectPattern.crossModuleDeps).toContain(`^${phase}`);
              }
            });
          }
        });
      }
    });
  });

  describe('Dependency Type Analysis', () => {
    it('should categorize different types of dependencies', () => {
      const dependencyTypes = new Map();

      projectDependencies.forEach(dep => {
        if (!dependencyTypes.has(dep.type)) {
          dependencyTypes.set(dep.type, []);
        }
        dependencyTypes.get(dep.type).push(dep);
      });

      console.log('Dependencies by type:');
      dependencyTypes.forEach((deps, type) => {
        console.log(`  ${type}: ${deps.length} dependencies`);

        // Show a few examples
        deps.slice(0, 3).forEach(dep => {
          console.log(`    ${dep.source} -> ${dep.target}`);
        });
      });

      // Should have static dependencies for Maven module relationships
      expect(dependencyTypes.has('static')).toBe(true);
    });

    it('should validate dependency metadata', () => {
      projectDependencies.forEach(dep => {
        expect(dep.source).toBeDefined();
        expect(dep.target).toBeDefined();
        expect(dep.type).toBeDefined();

        // Source and target should be non-empty strings
        expect(typeof dep.source).toBe('string');
        expect(typeof dep.target).toBe('string');
        expect(dep.source.length).toBeGreaterThan(0);
        expect(dep.target.length).toBeGreaterThan(0);
      });
    });
  });

  describe('Performance Impact of Cross-Module Dependencies', () => {
    it('should analyze dependency graph complexity', () => {
      const dependencyGraph = new Map();
      projectDependencies.forEach(dep => {
        if (!dependencyGraph.has(dep.source)) {
          dependencyGraph.set(dep.source, new Set());
        }
        dependencyGraph.get(dep.source).add(dep.target);
      });

      console.log('Dependency graph complexity analysis:');
      console.log(`  Total nodes: ${dependencyGraph.size}`);
      console.log(`  Total edges: ${projectDependencies.length}`);

      const avgDependencies = projectDependencies.length / dependencyGraph.size;
      console.log(`  Average dependencies per node: ${avgDependencies.toFixed(2)}`);

      // Find nodes with highest out-degree
      const outDegrees = Array.from(dependencyGraph.entries())
        .map(([node, targets]) => ({ node, degree: targets.size }))
        .sort((a, b) => b.degree - a.degree)
        .slice(0, 5);

      console.log('  Nodes with highest out-degree:');
      outDegrees.forEach(({ node, degree }) => {
        console.log(`    ${node}: ${degree} dependencies`);
      });

      // No single node should have excessive dependencies
      outDegrees.forEach(({ degree }) => {
        expect(degree).toBeLessThan(50); // Reasonable limit
      });
    });

    it('should validate parallel execution potential', () => {
      if (!quarkusCoreProject?.targets) return;

      // Count targets that can run in parallel vs sequential
      const parallelizableTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => target?.parallelism === true);

      const totalTargets = Object.keys(quarkusCoreProject.targets).length;
      const parallelPercentage = (parallelizableTargets.length / totalTargets) * 100;

      console.log(`Parallelization analysis:`);
      console.log(`  Total targets: ${totalTargets}`);
      console.log(`  Parallelizable targets: ${parallelizableTargets.length}`);
      console.log(`  Parallelization percentage: ${parallelPercentage.toFixed(1)}%`);

      // Most targets should be parallelizable
      expect(parallelPercentage).toBeGreaterThan(80);
    });
  });
});

