import { describe, it, expect, beforeAll } from 'vitest';
import { createNodesV2 } from './maven-plugin';
import { CreateNodesContextV2 } from '@nx/devkit';

describe('Target Inputs and Outputs Configuration Analysis', () => {
  const workspaceRoot = __dirname;
  let allProjects: Map<string, any>;
  let quarkusCoreProject: any;

  beforeAll(async () => {
    console.log('Analyzing target inputs and outputs configuration...');

    const nodesContext: CreateNodesContextV2 = {
      nxJsonConfiguration: {},
      workspaceRoot,
      configFiles: ['pom.xml']
    };

    try {
      const nodesResults = await createNodesV2[1](['pom.xml'], {}, nodesContext);

      allProjects = new Map();
      nodesResults.forEach(result => {
        if (result.nodes) {
          Object.entries(result.nodes).forEach(([key, node]) => {
            allProjects.set(key, node);
          });
        }
      });

      quarkusCoreProject = allProjects.get('io.quarkus:quarkus-core');
      console.log(`Found ${allProjects.size} projects, quarkus-core exists: ${!!quarkusCoreProject}`);
    } catch (error) {
      console.warn('Analysis failed:', error.message);
    }
  }, 180000);

  describe('Input Configuration Analysis', () => {
    it('should have pom.xml as input for all Maven targets', () => {
      if (!quarkusCoreProject?.targets) return;

      const mavenTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => 
          target?.metadata?.technologies?.includes('maven')
        );

      console.log('Maven targets and their pom.xml input configuration:');
      mavenTargets.forEach(([name, target]) => {
        const inputs = target?.inputs || [];
        const hasPomInput = inputs.includes('{projectRoot}/pom.xml');

        console.log(`  ${name}: has pom.xml input = ${hasPomInput}`);
        expect(hasPomInput).toBe(true);
      });
    });

    it('should have source inputs for compilation targets', () => {
      if (!quarkusCoreProject?.targets) return;

      const compilationTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([name, _]: [string, any]) => 
          name.includes('compile') || name.includes('resources')
        );

      console.log('Compilation targets and their source inputs:');
      compilationTargets.forEach(([name, target]) => {
        const inputs = target?.inputs || [];
        const hasSourceInputs = inputs.some((input: string) => 
          input.includes('src/**/*')
        );

        console.log(`  ${name}: inputs = [${inputs.join(', ')}]`);
        console.log(`    has source inputs = ${hasSourceInputs}`);

        if (name.includes('compile')) {
          expect(hasSourceInputs).toBe(true);
        }
      });
    });

    it('should differentiate between main and test source inputs', () => {
      if (!quarkusCoreProject?.targets) return;

      const mainCompileTarget = quarkusCoreProject.targets['maven-compiler:compile'];
      const testCompileTarget = quarkusCoreProject.targets['maven-compiler:testCompile'];

      if (mainCompileTarget) {
        const mainInputs = mainCompileTarget?.inputs || [];
        console.log('Main compilation inputs:', mainInputs);

        // Should include main source patterns
        const hasMainSources = mainInputs.some((input: string) => 
          input.includes('src/**/*')
        );
        expect(hasMainSources).toBe(true);
      }

      if (testCompileTarget) {
        const testInputs = testCompileTarget?.inputs || [];
        console.log('Test compilation inputs:', testInputs);

        // Should include test source patterns
        const hasTestSources = testInputs.some((input: string) => 
          input.includes('src/**/*')
        );
        expect(hasTestSources).toBe(true);
      }
    });

    it('should have empty inputs for lifecycle phases', () => {
      if (!quarkusCoreProject?.targets) return;

      const phaseTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => target?.metadata?.type === 'phase');

      console.log('Lifecycle phases inputs configuration:');
      phaseTargets.forEach(([name, target]) => {
        const inputs = target?.inputs || [];

        console.log(`  ${name}: inputs = [${inputs.join(', ')}]`);

        // Phases are orchestrators and should have empty inputs
        expect(inputs).toEqual([]);
      });
    });

    it('should have appropriate inputs for development targets', () => {
      if (!quarkusCoreProject?.targets) return;

      const devTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([name, _]: [string, any]) => 
          name.includes(':dev') || name.includes(':run')
        );

      console.log('Development targets inputs configuration:');
      devTargets.forEach(([name, target]) => {
        const inputs = target?.inputs || [];

        console.log(`  ${name}: inputs = [${inputs.join(', ')}]`);

        // Dev targets should watch source files
        const hasSourceInputs = inputs.some((input: string) => 
          input.includes('src/**/*')
        );

        if (inputs.length > 0) {
          expect(hasSourceInputs).toBe(true);
        }
      });
    });
  });

  describe('Output Configuration Analysis', () => {
    it('should have target directory outputs for build targets', () => {
      if (!quarkusCoreProject?.targets) return;

      const buildTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([name, _]: [string, any]) => 
          name.includes('compile') || name.includes('jar') || name.includes('build')
        );

      console.log('Build targets and their output configuration:');
      buildTargets.forEach(([name, target]) => {
        const outputs = target?.outputs || [];
        const hasTargetOutput = outputs.some((output: string) => 
          output.includes('{projectRoot}/target')
        );

        console.log(`  ${name}: outputs = [${outputs.join(', ')}]`);
        console.log(`    has target directory output = ${hasTargetOutput}`);

        if (outputs.length > 0) {
          expect(hasTargetOutput).toBe(true);
        }
      });
    });

    it('should have test report outputs for testing targets', () => {
      if (!quarkusCoreProject?.targets) return;

      const testTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([name, _]: [string, any]) => 
          name.includes('test') && !name.includes('compile')
        );

      console.log('Test targets and their report outputs:');
      testTargets.forEach(([name, target]) => {
        const outputs = target?.outputs || [];
        const hasTestReports = outputs.some((output: string) => 
          output.includes('surefire-reports') || output.includes('failsafe-reports')
        );

        console.log(`  ${name}: outputs = [${outputs.join(', ')}]`);

        if (outputs.length > 0) {
          console.log(`    has test report outputs = ${hasTestReports}`);
        }
      });
    });

    it('should have site outputs for documentation targets', () => {
      if (!quarkusCoreProject?.targets) return;

      const siteTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([name, _]: [string, any]) => name.includes('site'));

      console.log('Site targets and their output configuration:');
      siteTargets.forEach(([name, target]) => {
        const outputs = target?.outputs || [];
        const hasSiteOutput = outputs.some((output: string) => 
          output.includes('target/site')
        );

        console.log(`  ${name}: outputs = [${outputs.join(', ')}]`);

        if (outputs.length > 0) {
          expect(hasSiteOutput).toBe(true);
        }
      });
    });

    it('should have empty outputs for lifecycle phases', () => {
      if (!quarkusCoreProject?.targets) return;

      const phaseTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => target?.metadata?.type === 'phase');

      console.log('Lifecycle phases outputs configuration:');
      phaseTargets.forEach(([name, target]) => {
        const outputs = target?.outputs || [];

        console.log(`  ${name}: outputs = [${outputs.join(', ')}]`);

        // Phases are orchestrators and should have empty outputs
        expect(outputs).toEqual([]);
      });
    });

    it('should have empty outputs for deployment targets', () => {
      if (!quarkusCoreProject?.targets) return;

      const deploymentTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([name, _]: [string, any]) => 
          name.includes('deploy') || name.includes('install')
        );

      console.log('Deployment targets outputs configuration:');
      deploymentTargets.forEach(([name, target]) => {
        const outputs = target?.outputs || [];

        console.log(`  ${name}: outputs = [${outputs.join(', ')}]`);

        // Deployment targets typically don't produce local outputs
        if (name.includes('maven-deploy:deploy') || name.includes('maven-install:install')) {
          expect(outputs).toEqual([]);
        }
      });
    });
  });

  describe('Input/Output Pattern Validation', () => {
    it('should use projectRoot placeholders correctly', () => {
      if (!quarkusCoreProject?.targets) return;

      Object.entries(quarkusCoreProject.targets).forEach(([name, target]) => {
        const inputs = target?.inputs || [];
        const outputs = target?.outputs || [];

        [...inputs, ...outputs].forEach((path: string) => {
          if (path.includes('projectRoot')) {
            // Should use proper placeholder syntax
            expect(path).toMatch(/\{projectRoot\}/);
            console.log(` ${name} uses correct projectRoot placeholder: ${path}`);
          }
        });
      });
    });

    it('should have consistent input/output patterns across similar targets', () => {
      if (!quarkusCoreProject?.targets) return;

      // Group targets by type
      const targetGroups = {
        compilation: Object.entries(quarkusCoreProject.targets)
          .filter(([name]) => name.includes('compile')),
        testing: Object.entries(quarkusCoreProject.targets)
          .filter(([name]) => name.includes('test') && !name.includes('compile')),
        packaging: Object.entries(quarkusCoreProject.targets)
          .filter(([name]) => name.includes('jar') || name.includes('package'))
      };

      Object.entries(targetGroups).forEach(([groupName, targets]) => {
        if (targets.length > 1) {
          console.log(`\n${groupName} targets input/output patterns:`);

          targets.forEach(([name, target]) => {
            const inputs = target?.inputs || [];
            const outputs = target?.outputs || [];

            console.log(`  ${name}:`);
            console.log(`    inputs: [${inputs.join(', ')}]`);
            console.log(`    outputs: [${outputs.join(', ')}]`);
          });

          // All targets in group should have pom.xml input
          targets.forEach(([name, target]) => {
            const inputs = target?.inputs || [];
            expect(inputs).toContain('{projectRoot}/pom.xml');
          });
        }
      });
    });

    it('should validate input/output relationship for cached targets', () => {
      if (!quarkusCoreProject?.targets) return;

      const cacheableTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => {
          const inputs = target?.inputs || [];
          const outputs = target?.outputs || [];
          return inputs.length > 0 && outputs.length > 0;
        });

      console.log('Cacheable targets (have both inputs and outputs):');
      cacheableTargets.forEach(([name, target]) => {
        const inputs = target?.inputs || [];
        const outputs = target?.outputs || [];

        console.log(`  ${name}:`);
        console.log(`    inputs: [${inputs.join(', ')}]`);
        console.log(`    outputs: [${outputs.join(', ')}]`);

        // Should have meaningful inputs and outputs for caching
        expect(inputs.length).toBeGreaterThan(0);
        expect(outputs.length).toBeGreaterThan(0);
      });

      expect(cacheableTargets.length).toBeGreaterThan(0);
    });
  });

  describe('Performance Optimization Analysis', () => {
    it('should identify targets optimized for incremental builds', () => {
      if (!quarkusCoreProject?.targets) return;

      const incrementalTargets = Object.entries(quarkusCoreProject.targets)
        .filter(([_, target]: [string, any]) => {
          const inputs = target?.inputs || [];
          const outputs = target?.outputs || [];

          // Incremental targets should have specific source inputs and outputs
          const hasSpecificInputs = inputs.some((input: string) => 
            input.includes('src/**/*') || input.includes('pom.xml')
          );
          const hasSpecificOutputs = outputs.some((output: string) => 
            output.includes('target')
          );

          return hasSpecificInputs && hasSpecificOutputs;
        });

      console.log('Targets optimized for incremental builds:');
      incrementalTargets.forEach(([name, target]) => {
        const inputs = target?.inputs || [];
        const outputs = target?.outputs || [];

        console.log(`  ${name}: ${inputs.length} inputs, ${outputs.length} outputs`);
      });

      expect(incrementalTargets.length).toBeGreaterThan(0);
    });

    it('should analyze caching effectiveness patterns', () => {
      if (!quarkusCoreProject?.targets) return;

