export const createNodes: CreateNodes = [
  'pom.xml',
  (configFilePath, options, context) => {
    const opts = { ...DEFAULT_OPTIONS, ...options };
    const projectRoot = dirname(configFilePath);

    try {
      // Generate Nx configuration using the Java Maven analyzer
      const nxConfig = generateNxConfigFromMaven(configFilePath, opts, context);

      if (!nxConfig) {
        return {};
      }

      return {
        projects: {
          [projectRoot]: {
            name: nxConfig.name,
            root: projectRoot,
            sourceRoot: nxConfig.sourceRoot || join(projectRoot, 'src/main/java'),
            projectType: nxConfig.projectType || 'library',
            targets: nxConfig.targets || {},
            tags: nxConfig.tags || [],
            implicitDependencies: nxConfig.implicitDependencies,
            namedInputs: nxConfig.namedInputs,
          },
        },
      };
    } catch (error) {
      console.warn(`Failed to process Maven project at ${configFilePath}:`, error);
      return {};
    }
  },
];

/**
 * Create dependencies based on Maven dependency analysis
 */
export const createDependencies: CreateDependencies = (
  _,
  context
) => {
  const dependencies: ReturnType<CreateDependencies> = [];

  // Find all Maven projects in the workspace
  const mavenProjects = findMavenProjects(context);

  for (const projectPath of mavenProjects) {
    try {
      const projectConfig = generateNxConfigFromMaven(
        join(projectPath, 'pom.xml'),
        DEFAULT_OPTIONS,
        context
      );

      if (!projectConfig?.implicitDependencies) {
        continue;
      }

      const sourceProjectName = getProjectName(projectPath, context);
      if (!sourceProjectName) continue;

      // Add project dependencies
      const projectDeps = projectConfig.implicitDependencies.projects || [];
      for (const depName of projectDeps) {
        const targetProject = findProjectByName(depName, mavenProjects, context);
        if (targetProject) {
          dependencies.push({
            source: sourceProjectName,
            target: targetProject,
            type: 'static',
          });
        }
      }

      // Add parent dependencies
      const parentDeps = projectConfig.implicitDependencies.inheritsFrom || [];
      for (const parentName of parentDeps) {
        const parentProject = findProjectByName(parentName, mavenProjects, context);
        if (parentProject) {
          dependencies.push({
            source: sourceProjectName,
            target: parentProject,
            type: 'implicit',
