import {
  CreateDependencies,
  CreateNodesV2,
  CreateNodesContext,
  CreateNodesResult,
  detectPackageManager,
  NxJsonConfiguration,
  TargetConfiguration,
  workspaceRoot,
} from '@nx/devkit';
import { dirname, join, relative } from 'path';
import { existsSync, readFileSync } from 'fs';
import { execSync } from 'child_process';

export interface MavenPluginOptions {
  buildTargetName?: string;
  testTargetName?: string;
  serveTargetName?: string;
  javaExecutable?: string;
  mavenExecutable?: string;
  compilerArgs?: string[];
}

const DEFAULT_OPTIONS: MavenPluginOptions = {
  buildTargetName: 'build',
  testTargetName: 'test', 
  serveTargetName: 'serve',
  javaExecutable: 'java',
  mavenExecutable: 'mvn',
  compilerArgs: [],
};

/**
 * Maven plugin that uses a Java program to analyze pom.xml files
 * and generate Nx project configurations with comprehensive dependencies
 */
export const createNodes: CreateNodes = [
  'pom.xml',
  (configFilePath, options, context) => {
    const opts = { ...DEFAULT_OPTIONS, ...options };
    const projectRoot = dirname(configFilePath);

    try {
      // Generate Nx configuration using the Java Maven analyzer
      const nxConfig = generateNxConfigFromMaven(configFilePath, opts, context);

      if (!nxConfig) {
        return {};
      }

      return {
        projects: {
          [projectRoot]: {
            name: nxConfig.name,
            root: projectRoot,
            sourceRoot: nxConfig.sourceRoot || join(projectRoot, 'src/main/java'),
            projectType: nxConfig.projectType || 'library',
            targets: nxConfig.targets || {},
            tags: nxConfig.tags || [],
            implicitDependencies: nxConfig.implicitDependencies,
            namedInputs: nxConfig.namedInputs,
          },
        },
      };
    } catch (error) {
      console.warn(`Failed to process Maven project at ${configFilePath}:`, error);
      return {};
    }
  },
];

/**
 * Create dependencies based on Maven dependency analysis
 */
export const createDependencies: CreateDependencies = (
  _,
  context
) => {
  const dependencies: ReturnType<CreateDependencies> = [];

  // Find all Maven projects in the workspace
  const mavenProjects = findMavenProjects(context);

  for (const projectPath of mavenProjects) {
    try {
      const projectConfig = generateNxConfigFromMaven(
        join(projectPath, 'pom.xml'),
        DEFAULT_OPTIONS,
        context
      );

      if (!projectConfig?.implicitDependencies) {
        continue;
      }

      const sourceProjectName = getProjectName(projectPath, context);
      if (!sourceProjectName) continue;

      // Add project dependencies
      const projectDeps = projectConfig.implicitDependencies.projects || [];
      for (const depName of projectDeps) {
        const targetProject = findProjectByName(depName, mavenProjects, context);
        if (targetProject) {
          dependencies.push({
            source: sourceProjectName,
            target: targetProject,
            type: 'static',
          });
        }
      }

      // Add parent dependencies
      const parentDeps = projectConfig.implicitDependencies.inheritsFrom || [];
      for (const parentName of parentDeps) {
        const parentProject = findProjectByName(parentName, mavenProjects, context);
        if (parentProject) {
          dependencies.push({
            source: sourceProjectName,
            target: parentProject,
            type: 'implicit',
          });
        }
      }

    } catch (error) {
      console.warn(`Failed to analyze dependencies for ${projectPath}:`, error);
    }
  }

  return dependencies;
};

/**
 * Generate Nx configuration by calling the Java Maven analyzer
 */
function generateNxConfigFromMaven(
  pomPath: string,
  options: MavenPluginOptions,
  context: CreateNodesContext
): any {
  const javaAnalyzerPath = findJavaAnalyzer();
  if (!javaAnalyzerPath) {
    throw new Error('Maven analyzer Java program not found. Please ensure maven-script is compiled.');
  }

  try {
    // Call the Java program to generate Nx configuration
    const command = `${options.javaExecutable} -cp "${javaAnalyzerPath}" MavenModelReader "${pomPath}" --nx`;
    const output = execSync(command, {
      cwd: workspaceRoot,
      encoding: 'utf8',
      timeout: 30000, // 30 second timeout
    });

    // Parse JSON output from the Java program
    const jsonStart = output.indexOf('{');
    const jsonEnd = output.lastIndexOf('}') + 1;

    if (jsonStart === -1 || jsonEnd === 0) {
      throw new Error('No valid JSON output from Maven analyzer');
    }

    const jsonOutput = output.substring(jsonStart, jsonEnd);
    return JSON.parse(jsonOutput);

  } catch (error) {
    throw new Error(`Failed to execute Maven analyzer: ${error.message}`);
  }
}

/**
 * Find the compiled Java Maven analyzer
 */
function findJavaAnalyzer(): string | null {
  // Look for the compiled Java program in common locations
  const possiblePaths = [
    join(workspaceRoot, 'maven-script/target/classes'),
    join(workspaceRoot, 'tools/maven-script/target/classes'),
    join(workspaceRoot, 'scripts/maven-script/target/classes'),
    join(__dirname, '../maven-script/target/classes'),
  ];

  for (const path of possiblePaths) {
    if (existsSync(join(path, 'MavenModelReader.class'))) {
      return path;
    }
  }

  // Also check if there's a JAR file
  const jarPaths = [
    join(workspaceRoot, 'maven-script/target/maven-script-1.0-SNAPSHOT.jar'),
    join(workspaceRoot, 'tools/maven-script/target/maven-script-1.0-SNAPSHOT.jar'),
  ];

  for (const jarPath of jarPaths) {
    if (existsSync(jarPath)) {
      return jarPath;
    }
  }

  return null;
}

/**
 * Find all Maven projects in the workspace
 */
function findMavenProjects(context: CreateNodesContext): string[] {
  const projects: string[] = [];

  // Use Nx's file system utilities if available
  if (context.fileMap) {
    for (const [projectRoot, files] of Object.entries(context.fileMap)) {
      if (files.some(file => file.file === 'pom.xml')) {
        projects.push(projectRoot);
      }
    }
  } else {
    // Fallback: scan workspace for pom.xml files
    try {
      const command = 'find . -name "pom.xml" -type f';
      const output = execSync(command, {
        cwd: workspaceRoot,
        encoding: 'utf8',
      });

      const pomFiles = output.trim().split('\n').filter(Boolean);
      for (const pomFile of pomFiles) {
        const projectPath = dirname(pomFile.replace('./', ''));
        if (projectPath !== '.') {
          projects.push(projectPath);
        }
      }
    } catch {
      // Ignore errors in fallback
    }
  }

  return projects;
}

/**
 * Get project name from path using Nx context
 */
function getProjectName(projectPath: string, context: CreateNodesContext): string | null {
  if (context.projectsConfigurations) {
    for (const [name, config] of Object.entries(context.projectsConfigurations.projects)) {
      if (config.root === projectPath) {
        return name;
      }
    }
  }

  // Fallback: use directory name
  return projectPath.split('/').pop() || null;
}

/**
 * Find a project by its Maven artifactId
 */
function findProjectByName(
  artifactId: string, 
  mavenProjects: string[], 
  context: CreateNodesContext
): string | null {
  // First try to find by exact project name match
  if (context.projectsConfigurations) {
    for (const [name, config] of Object.entries(context.projectsConfigurations.projects)) {
      if (name === artifactId && mavenProjects.includes(config.root)) {
        return name;
      }
    }
  }

  // Try to find by directory name
  for (const projectPath of mavenProjects) {
    const dirName = projectPath.split('/').pop();
    if (dirName === artifactId) {
      return getProjectName(projectPath, context) || dirName;
    }
  }

  // Try to find by parsing pom.xml artifactId
  for (const projectPath of mavenProjects) {
    try {
      const pomPath = join(workspaceRoot, projectPath, 'pom.xml');
      if (existsSync(pomPath)) {
        const pomContent = readFileSync(pomPath, 'utf8');
        const artifactIdMatch = pomContent.match(/<artifactId>([^<]+)<\/artifactId>/);
        if (artifactIdMatch && artifactIdMatch[1] === artifactId) {
          return getProjectName(projectPath, context) || projectPath.split('/').pop();
        }
      }
    } catch {
      // Ignore parsing errors
    }
  }

  return null;
}

/**
 * Normalize target configurations from Maven analysis
 */
function normalizeTargets(targets: Record<string, any>, options: MavenPluginOptions): Record<string, TargetConfiguration> {
  const normalizedTargets: Record<string, TargetConfiguration> = {};

  for (const [name, target] of Object.entries(targets)) {
    // Map common Maven targets to Nx conventions
    let targetName = name;
    if (name === 'package' && options.buildTargetName !== 'package') {
      targetName = options.buildTargetName!;
    } else if (name === 'test' && options.testTargetName !== 'test') {
      targetName = options.testTargetName!;
    } else if (name === 'serve' && options.serveTargetName !== 'serve') {
      targetName = options.serveTargetName!;
    }

    normalizedTargets[targetName] = {
      executor: target.executor || '@nx/run-commands:run-commands',
      options: target.options || {},
      inputs: target.inputs,
      outputs: target.outputs,
      dependsOn: target.dependsOn,
      configurations: target.configurations,
    };
  }

  return normalizedTargets;
}

/**
 * Plugin configuration for registration
 */
export default {
  name: 'maven-plugin2',
  createNodes,
  createDependencies,
};

