  const jarPaths = [
    join(workspaceRoot, 'maven-script/target/maven-script-1.0-SNAPSHOT.jar'),
    join(workspaceRoot, 'tools/maven-script/target/maven-script-1.0-SNAPSHOT.jar'),
  ];

