    return results.filter(result => result && typeof result === 'object' && result.projects);
  },
];

/**
 * Create dependencies based on Maven dependency analysis
 */
export const createDependencies: CreateDependencies = async (
  _,
  context
) => {
  const dependencies: ReturnType<CreateDependencies> = [];

  // Find all Maven projects in the workspace
  const mavenProjects = findMavenProjects(context);

  // Process projects in parallel for better performance
  const dependencyPromises = mavenProjects.map(async (projectPath) => {
    try {
      const projectConfig = await generateNxConfigFromMavenAsync(
        join(projectPath, 'pom.xml'),
        DEFAULT_OPTIONS,
        context
      );

      if (!projectConfig?.implicitDependencies) {
        return [];
      }

      const sourceProjectName = getProjectName(projectPath, context);
      if (!sourceProjectName) return [];

      const projectDependencies: ReturnType<CreateDependencies> = [];

      // Add project dependencies
      const projectDeps = projectConfig.implicitDependencies.projects || [];
      for (const depName of projectDeps) {
        const targetProject = findProjectByName(depName, mavenProjects, context);
        if (targetProject) {
          projectDependencies.push({
            source: sourceProjectName,
            target: targetProject,
            type: 'static',
          });
        }
      }

      // Add parent dependencies
      const parentDeps = projectConfig.implicitDependencies.inheritsFrom || [];
      for (const parentName of parentDeps) {
        const parentProject = findProjectByName(parentName, mavenProjects, context);
        if (parentProject) {
          projectDependencies.push({
            source: sourceProjectName,
            target: parentProject,
            type: 'implicit',
          });
        }
      }

      return projectDependencies;
    } catch (error) {
      console.warn(`Failed to analyze dependencies for ${projectPath}:`, error);
      return [];
    }
  });

  const allDependencies = await Promise.all(dependencyPromises);
  dependencies.push(...allDependencies.flat());

  return dependencies;
