import {
  CreateDependencies,
  CreateNodesV2,
  CreateNodesContext,
  CreateNodesResult,
  detectPackageManager,
  NxJsonConfiguration,
  TargetConfiguration,
  workspaceRoot,
} from '@nx/devkit';
import { dirname, join, relative } from 'path';
import { existsSync, readFileSync } from 'fs';
import { exec, execSync } from 'child_process';
import { promisify } from 'util';

const execAsync = promisify(exec);

export interface MavenPluginOptions {
  buildTargetName?: string;
  testTargetName?: string;
  serveTargetName?: string;
  javaExecutable?: string;
  mavenExecutable?: string;
  compilerArgs?: string[];
}

const DEFAULT_OPTIONS: MavenPluginOptions = {
  buildTargetName: 'build',
  testTargetName: 'test',
  serveTargetName: 'serve',
  javaExecutable: 'java',
  mavenExecutable: 'mvn',
  compilerArgs: [],
};

/**
 * Maven plugin that uses a Java program to analyze pom.xml files
 * and generate Nx project configurations with comprehensive dependencies
 */
export const createNodesV2: CreateNodesV2 = [
  '**/pom.xml',
  async (configFiles, options, context) => {
    const opts = { ...DEFAULT_OPTIONS, ...options };

    console.log(`All config files found: ${JSON.stringify(configFiles.slice(0, 10), null, 2)}`);

    // Filter out the maven-script pom.xml, workspace root, and build artifact directories
    const filteredConfigFiles = configFiles.filter(file =>
      !file.includes('maven-script/pom.xml') &&
      !file.includes('target/') &&
      !file.includes('node_modules/') &&
      file !== 'pom.xml' && // Exclude workspace root
      file.includes('/') // Only include sub-project POMs
    ).slice(0, 3); // Limit to first 3 projects for testing

    console.log(`Filtered config files: ${JSON.stringify(filteredConfigFiles, null, 2)}`);

    // Process all Maven projects in parallel for better performance
    const promises = filteredConfigFiles.map(async (configFilePath) => {
      const projectRoot = dirname(configFilePath);

      console.log(`Processing config file: ${configFilePath}, projectRoot: ${projectRoot}`);

      try {
        // Generate Nx configuration using the Java Maven analyzer
        const nxConfig = await generateNxConfigFromMavenAsync(configFilePath, opts, context);

        console.log(`Generated config for ${configFilePath}:`, JSON.stringify(nxConfig, null, 2));

        if (!nxConfig) {
          console.log(`No config generated for ${configFilePath}`);
          return { projects: {} };
        }

        // Normalize and validate targets
        const normalizedTargets = normalizeTargets(nxConfig.targets || {}, opts);

        const result = {
          projects: {
            [projectRoot]: {
              name: nxConfig.name || projectRoot.split('/').pop() || 'unknown',
              root: projectRoot,
              sourceRoot: nxConfig.sourceRoot || join(projectRoot, 'src/main/java'),
              projectType: nxConfig.projectType || 'library',
              targets: normalizedTargets,
              tags: nxConfig.tags || [],
              implicitDependencies: nxConfig.implicitDependencies,
              namedInputs: nxConfig.namedInputs,
              metadata: {
                technologies: ['maven', 'java'],
                framework: detectFramework(nxConfig),
              },
            },
          },
        };

        console.log(`Final result for ${configFilePath}:`, JSON.stringify(result, null, 2));
        return result;
      } catch (error) {
        console.warn(`Failed to process Maven project at ${configFilePath}:`, error.message);
        return { projects: {} };
      }
    });

    const results = await Promise.all(promises);
    // Filter out any null or invalid results
    const validResults = results.filter(result => result && typeof result === 'object' && result.projects);

    console.log(`Processed ${filteredConfigFiles.length} files, got ${validResults.length} valid results`);

    // CreateNodesV2 should return an array of results
    return validResults;
  },
];

/**
 * Create dependencies based on Maven dependency analysis
 */
export const createDependencies: CreateDependencies = async (
  _,
  context
) => {
  const dependencies: ReturnType<CreateDependencies> = [];

  // Find all Maven projects in the workspace
  const mavenProjects = findMavenProjects(context);

  // Process projects in parallel for better performance
  const dependencyPromises = mavenProjects.map(async (projectPath) => {
    try {
      const projectConfig = await generateNxConfigFromMavenAsync(
        join(projectPath, 'pom.xml'),
        DEFAULT_OPTIONS,
        context
      );

      if (!projectConfig?.implicitDependencies) {
        return [];
      }

      const sourceProjectName = getProjectName(projectPath, context);
      if (!sourceProjectName) return [];

      const projectDependencies: ReturnType<CreateDependencies> = [];

      // Add project dependencies
      const projectDeps = projectConfig.implicitDependencies.projects || [];
      for (const depName of projectDeps) {
        const targetProject = findProjectByName(depName, mavenProjects, context);
        if (targetProject) {
          projectDependencies.push({
            source: sourceProjectName,
            target: targetProject,
            type: 'static',
          });
        }
      }

      // Add parent dependencies
      const parentDeps = projectConfig.implicitDependencies.inheritsFrom || [];
      for (const parentName of parentDeps) {
        const parentProject = findProjectByName(parentName, mavenProjects, context);
        if (parentProject) {
          projectDependencies.push({
            source: sourceProjectName,
            target: parentProject,
            type: 'implicit',
          });
        }
      }

      return projectDependencies;
    } catch (error) {
      console.warn(`Failed to analyze dependencies for ${projectPath}:`, error);
      return [];
    }
  });

  const allDependencies = await Promise.all(dependencyPromises);
  dependencies.push(...allDependencies.flat());

  return dependencies;
};

/**
 * Generate Nx configuration by calling the Java Maven analyzer (async version)
 */
async function generateNxConfigFromMavenAsync(
  pomPath: string,
  options: MavenPluginOptions,
  context: CreateNodesContext
): Promise<any> {
  const javaAnalyzerPath = findJavaAnalyzer();
  if (!javaAnalyzerPath) {
    throw new Error('Maven analyzer Java program not found. Please ensure maven-script is compiled.');
  }

  try {
    // Use Maven exec to run the Java program with proper classpath
    const mavenScriptDir = javaAnalyzerPath.endsWith('.jar')
      ? dirname(javaAnalyzerPath)
      : dirname(dirname(javaAnalyzerPath)); // target/classes -> project root

    console.log(`Analyzing ${pomPath} using Maven script in ${mavenScriptDir}`);

    const command = `${options.mavenExecutable} exec:java -Dexec.mainClass="MavenModelReader" -Dexec.args="${pomPath} --nx" -q`;
    const { stdout, stderr } = await execAsync(command, {
      cwd: mavenScriptDir,
      timeout: 30000, // 30 second timeout
      maxBuffer: 1024 * 1024 * 10, // 10MB buffer for large outputs
    });

    if (stderr && stderr.trim()) {
      console.warn(`Maven analyzer stderr: ${stderr}`);
    }

    // Parse JSON output from the Java program
    const output = stdout;
    const jsonStart = output.indexOf('{');
    const jsonEnd = output.lastIndexOf('}') + 1;

    if (jsonStart === -1 || jsonEnd === 0) {
      throw new Error('No valid JSON output from Maven analyzer');
    }

    const jsonOutput = output.substring(jsonStart, jsonEnd);
    return JSON.parse(jsonOutput);

  } catch (error) {
    throw new Error(`Failed to execute Maven analyzer: ${error.message}`);
  }
}

/**
 * Build classpath including Maven dependencies
 */
function buildClasspath(javaAnalyzerPath: string): string {
  const classpathParts: string[] = [javaAnalyzerPath];

  // If it's a JAR file, just return it
  if (javaAnalyzerPath.endsWith('.jar')) {
    return javaAnalyzerPath;
  }

  // For compiled classes, we need to include Maven dependencies
  const mavenScriptDir = dirname(javaAnalyzerPath); // target/classes -> target
  const projectDir = dirname(mavenScriptDir);       // target -> project root

  try {
    // Use Maven to build dependency classpath
    const command = 'mvn dependency:build-classpath -q -Dmdep.outputFile=/dev/stdout';
    const dependencyClasspath = execSync(command, {
      cwd: projectDir,
      encoding: 'utf8',
      timeout: 30000,
    }).trim();

    if (dependencyClasspath) {
      classpathParts.push(dependencyClasspath);
    }
  } catch (error) {
    console.warn('Failed to build Maven dependency classpath, using basic classpath');
  }

  return classpathParts.join(process.platform === 'win32' ? ';' : ':');
}

/**
