    join(workspaceRoot, 'tools/maven-script/target/maven-script-1.0-SNAPSHOT.jar'),
  ];

  for (const jarPath of jarPaths) {
    if (existsSync(jarPath)) {
      return jarPath;
    }
  }

  return null;
}

/**
