    console.log(`Analyzing ${absolutePomPath} using Maven script in ${mavenScriptDir}`);

    const command = `${options.mavenExecutable} exec:java -Dexec.mainClass="MavenModelReader" -Dexec.args="${absolutePomPath} --nx" -q`;
