/**
 * Maven plugin that uses a Java program to analyze pom.xml files
 * and generate Nx project configurations with comprehensive dependencies
 */
export const createNodesV2: CreateNodesV2 = [
  '**/pom.xml',
  async (configFiles, options, context) => {
    const opts = { ...DEFAULT_OPTIONS, ...options };

    console.log(`All config files found: ${JSON.stringify(configFiles.slice(0, 10), null, 2)}`);

    // Filter out the maven-script pom.xml, workspace root, and build artifact directories
    const filteredConfigFiles = configFiles.filter(file =>
      !file.includes('maven-script/pom.xml') &&
      !file.includes('target/') &&
      !file.includes('node_modules/') &&
      file !== 'pom.xml' && // Exclude workspace root
      file.includes('/') // Only include sub-project POMs
    ).slice(0, 5); // Limit to first 5 projects for testing

    console.log(`Filtered config files: ${JSON.stringify(filteredConfigFiles, null, 2)}`);

    // Process all Maven projects in parallel for better performance
    const promises = filteredConfigFiles.map(async (configFilePath) => {
      const projectRoot = dirname(configFilePath);

      try {
        // Generate Nx configuration using the Java Maven analyzer
        const nxConfig = await generateNxConfigFromMavenAsync(configFilePath, opts, context);

        if (!nxConfig) {
          return { projects: {} };
        }

        // Normalize and validate targets
        const normalizedTargets = normalizeTargets(nxConfig.targets || {}, opts);

        const result = {
          projects: {
            [projectRoot]: {
              name: nxConfig.name || projectRoot.split('/').pop() || 'unknown',
              root: projectRoot,
              sourceRoot: nxConfig.sourceRoot || join(projectRoot, 'src/main/java'),
              projectType: nxConfig.projectType || 'library',
              targets: normalizedTargets,
              tags: nxConfig.tags || [],
              implicitDependencies: nxConfig.implicitDependencies,
              namedInputs: nxConfig.namedInputs,
              metadata: {
                technologies: ['maven', 'java'],
                framework: detectFramework(nxConfig),
              },
            },
          },
        };

        return result;
      } catch (error) {
        console.warn(`Failed to process Maven project at ${configFilePath}:`, error.message);
        return { projects: {} };
      }
    });

    const results = await Promise.all(promises);
    // Filter out any null or invalid results
    const validResults = results.filter(result => result && typeof result === 'object' && result.projects);

    console.log(`Processed ${filteredConfigFiles.length} files, got ${validResults.length} valid results`);

    // CreateNodesV2 should return an array of results
    return validResults;
  },
];

/**
