// Use spawn to pass input via stdin
const inputData = absolutePomPaths.join('\n') + '\n';

const { stdout, stderr } = await new Promise<{stdout: string, stderr: string}>((resolve, reject) => {
  const child = spawn(options.mavenExecutable, [
    'exec:java',
    '-Dexec.mainClass=MavenModelReader',
    '-Dexec.args=--stdin --nx',
    '-q'
  ], {
    cwd: mavenScriptDir,
    stdio: ['pipe', 'pipe', 'pipe']
  });

  let stdout = '';
  let stderr = '';

  child.stdout?.on('data', (data) => {
    stdout += data.toString();
  });

  child.stderr?.on('data', (data) => {
    stderr += data.toString();
  });

  child.on('close', (code) => {
    if (code === 0) {
      resolve({ stdout, stderr });
    } else {
      reject(new Error(`Maven process exited with code ${code}. stderr: ${stderr}`));
    }
  });

  child.on('error', (error) => {
    reject(new Error(`Failed to spawn Maven process: ${error.message}`));
  });

  // Write input data to stdin and close it
  child.stdin?.write(inputData);
  child.stdin?.end();

  // Set timeout
  setTimeout(() => {
    child.kill();
    reject(new Error('Maven process timed out after 2 minutes'));
  }, 120000);
});

