    options.compileTargetName ??= 'compile';
    options.packageTargetName ??= 'package';
    options.installTargetName ??= 'install';
    return options;
}

type MavenTargets = Record<string, Partial<ProjectConfiguration>>;

function readTargetsCache(cachePath: string): MavenTargets {
    return existsSync(cachePath) ? readJsonFile(cachePath) : {};
}

function writeTargetsToCache(cachePath: string, results: MavenTargets) {
    writeJsonFile(cachePath, results);
}

export const createNodesV2 = [
    MAVEN_CONFIG_AND_TEST_GLOB,
    async (files, options, context) => {
        const { pomFiles, testFiles } = splitConfigFiles(files);
        const optionsHash = hashObject(options);
        const cachePath = join(workspaceDataDirectory, `maven-${optionsHash}.hash`);
        const targetsCache = readTargetsCache(cachePath);

        const mavenReport = await parsePomXml(context.workspaceRoot, pomFiles);
        const mavenProjectRootToTestFilesMap = getMavenProjectRootToTestFilesMap(
            testFiles,
            pomFiles.map((f) => dirname(f))
        );

        try {
            return createNodesFromFiles(
                makeCreateNodesForMavenConfigFile(
                    mavenReport,
                    targetsCache,
                    mavenProjectRootToTestFilesMap
                ),
                pomFiles,
                options,
                context
            );
        } finally {
            writeTargetsToCache(cachePath, targetsCache);
        }
    },
];

export const makeCreateNodesForMavenConfigFile =
    (
        mavenReport: MavenReport,
        targetsCache: MavenTargets = {},
        mavenProjectRootToTestFilesMap: Record<string, string[]> = {}
    ): CreateNodesFunction =>
        async (
            pomFilePath,
            options: MavenPluginOptions | undefined,
            context: CreateNodesContext
        ) => {
            const projectRoot = dirname(pomFilePath);
            options = normalizeOptions(options);

            const hash = await calculateHashForCreateNodes(
                projectRoot,
                options ?? {},
                context
            );
            targetsCache[hash] ??= await createMavenProject(
                mavenReport,
                pomFilePath,
                options,
                context,
                mavenProjectRootToTestFilesMap[projectRoot]
            );
            const project = targetsCache[hash];
            if (!project) {
                return {};
            }
            return {
                projects: {
                    [projectRoot]: project,
                },
            };
        };

async function createMavenProject(
    mavenReport: MavenReport,
    pomFilePath: string,
    options: MavenPluginOptions | undefined,
    context: CreateNodesContext,
    testFiles: string[] = []
) {
    try {
        const projectInfo = mavenReport.projects.get(pomFilePath);
        if (!projectInfo) {
            return;
        }

        const { targets, targetGroups } = await createMavenTargets(
            projectInfo,
            options,
            context,
            pomFilePath,
            testFiles
        );

        const project: Partial<ProjectConfiguration> = {
            name: `${projectInfo.groupId ?? projectInfo.parent.groupId}.${
                projectInfo.artifactId
            }`,
            projectType: 'application',
            targets,
            metadata: {
                targetGroups,
                technologies: ['maven'],
            },
        };

        return project;
    } catch (e) {
        console.error(e);
        return undefined;
    }
}

async function createMavenTargets(
    projectInfo: MavenProjectInfo,
    options: MavenPluginOptions | undefined,
    context: CreateNodesContext,
    pomFilePath: string,
    testFiles: string[] = []
): Promise<{
    targetGroups: Record<string, string[]>;
    targets: Record<string, TargetConfiguration>;
}> {
    const targets: Record<string, TargetConfiguration> = {};
    const targetGroups: Record<string, string[]> = {
        build: [],
        test: [],
        dev: [],
        quality: [],
    };

    const projectRoot = dirname(pomFilePath);
    const hasTests = testFiles.length > 0;
    const isQuarkusProject = hasQuarkusPlugin(projectInfo);
    const isSpringBootProject = hasSpringBootPlugin(projectInfo);

    // Create enhanced inputs/outputs based on project structure
    const commonInputs = getProjectInputs(projectInfo, projectRoot);
    const commonOutputs = getProjectOutputs(projectInfo, projectRoot);

    // Add standard Maven lifecycle targets with enhanced configuration
    const lifecycleTargets = [
        {
            name: options.compileTargetName,
            phase: 'compile',
            group: 'build',
            outputs: ['{projectRoot}/target/classes'],
        },
        {
            name: options.testTargetName,
            phase: 'test',
            group: 'test',
            outputs: hasTests ? ['{projectRoot}/target/test-results', '{projectRoot}/target/surefire-reports'] : undefined,
            condition: hasTests,
        },
        {
            name: options.packageTargetName,
            phase: 'package',
            group: 'build',
            outputs: [`{projectRoot}/target/*.${projectInfo.packaging || 'jar'}`],
        },
        {
            name: options.verifyTargetName,
            phase: 'verify',
            group: 'quality',
            outputs: undefined,
        },
        {
            name: options.installTargetName,
            phase: 'install',
            group: 'build',
            outputs: undefined,
        },
    ];

    // Add lifecycle targets
    for (const target of lifecycleTargets) {
        if (target.condition === false) continue;

        const targetConfig: TargetConfiguration = {
            command: `mvn ${target.phase}`,
            options: {
                cwd: projectRoot,
            },
            cache: true,
            inputs: commonInputs,
            outputs: target.outputs,
            metadata: {
                technologies: ['maven'],
                description: `Run Maven ${target.phase} phase`,
            },
        };

        targets[target.name] = targetConfig;
        targetGroups[target.group].push(target.name);
    }

    // Add framework-specific targets
    if (isQuarkusProject) {
        addQuarkusTargets(targets, targetGroups, projectRoot, options);
    }

    if (isSpringBootProject) {
        addSpringBootTargets(targets, targetGroups, projectRoot, options);
    }

    // Add plugin-specific targets based on Maven plugins
    addPluginSpecificTargets(projectInfo, targets, targetGroups, projectRoot, options);

    return { targets, targetGroups };
}

function hasQuarkusPlugin(projectInfo: MavenProjectInfo): boolean {
    return projectInfo.plugins?.some(
        plugin => plugin.groupId === 'io.quarkus' && plugin.artifactId === 'quarkus-maven-plugin'
    ) || false;
}

function hasSpringBootPlugin(projectInfo: MavenProjectInfo): boolean {
    return projectInfo.plugins?.some(
        plugin => plugin.groupId === 'org.springframework.boot' && plugin.artifactId === 'spring-boot-maven-plugin'
    ) || false;
}

function getProjectInputs(projectInfo: MavenProjectInfo, projectRoot: string): string[] {
    const inputs = ['default', '^production'];

    // Add source directories
    inputs.push('{projectRoot}/src/main/**/*');

    // Add test directories if they exist
    inputs.push('{projectRoot}/src/test/**/*');

    // Add resources
    inputs.push('{projectRoot}/src/main/resources/**/*');

    return inputs;
}

function getProjectOutputs(projectInfo: MavenProjectInfo, projectRoot: string): string[] {
    return [
        '{projectRoot}/target/**/*',
    ];
}

function addQuarkusTargets(
    targets: Record<string, TargetConfiguration>,
    targetGroups: Record<string, string[]>,
    projectRoot: string,
    options: MavenPluginOptions | undefined
): void {
    // Quarkus dev mode
    targets['dev'] = {
        command: 'mvn quarkus:dev',
        options: { cwd: projectRoot },
        cache: false,
        inputs: ['{projectRoot}/src/**/*', '{projectRoot}/pom.xml'],
        metadata: {
            technologies: ['maven', 'quarkus'],
            description: 'Start Quarkus in development mode',
        },
    };
    targetGroups.dev.push('dev');

    // Quarkus native build
    targets['build-native'] = {
        command: 'mvn package -Dnative',
        options: { cwd: projectRoot },
        cache: true,
        inputs: ['{projectRoot}/src/**/*', '{projectRoot}/pom.xml'],
        outputs: ['{projectRoot}/target/*-runner'],
        metadata: {
            technologies: ['maven', 'quarkus'],
            description: 'Build native executable with Quarkus',
        },
    };
    targetGroups.build.push('build-native');
}

function addSpringBootTargets(
    targets: Record<string, TargetConfiguration>,
    targetGroups: Record<string, string[]>,
    projectRoot: string,
    options: MavenPluginOptions | undefined
): void {
    // Spring Boot run
    targets['run'] = {
        command: 'mvn spring-boot:run',
        options: { cwd: projectRoot },
        cache: false,
        inputs: ['{projectRoot}/src/**/*', '{projectRoot}/pom.xml'],
        metadata: {
            technologies: ['maven', 'spring-boot'],
            description: 'Run Spring Boot application',
        },
    };
    targetGroups.dev.push('run');
}

function addPluginSpecificTargets(
    projectInfo: MavenProjectInfo,
    targets: Record<string, TargetConfiguration>,
    targetGroups: Record<string, string[]>,
    projectRoot: string,
    options: MavenPluginOptions | undefined
): void {
    if (!projectInfo.plugins) return;

    for (const plugin of projectInfo.plugins) {
        // Flyway plugin
        if (plugin.groupId === 'org.flywaydb' && plugin.artifactId === 'flyway-maven-plugin') {
            targets['db-migrate'] = {
                command: 'mvn flyway:migrate',
                options: { cwd: projectRoot },
                cache: false,
                inputs: ['{projectRoot}/src/main/resources/db/migration/**/*'],
                metadata: {
                    technologies: ['maven', 'flyway'],
                    description: 'Run database migrations',
                },
            };
            targetGroups.build.push('db-migrate');
        }

        // Liquibase plugin
        if (plugin.groupId === 'org.liquibase' && plugin.artifactId === 'liquibase-maven-plugin') {
            targets['db-update'] = {
                command: 'mvn liquibase:update',
                options: { cwd: projectRoot },
                cache: false,
                inputs: ['{projectRoot}/src/main/resources/db/changelog/**/*'],
                metadata: {
                    technologies: ['maven', 'liquibase'],
                    description: 'Update database schema',
                },
            };
            targetGroups.build.push('db-update');
        }

        // Spotless plugin for code formatting
        if (plugin.groupId === 'com.diffplug.spotless' && plugin.artifactId === 'spotless-maven-plugin') {
            targets['format'] = {
                command: 'mvn spotless:apply',
                options: { cwd: projectRoot },
                cache: false,
                inputs: ['{projectRoot}/src/**/*'],
                metadata: {
                    technologies: ['maven', 'spotless'],
                    description: 'Format code with Spotless',
                },
            };
            targetGroups.quality.push('format');
        }

        // Maven Checkstyle plugin
        if (plugin.groupId === 'org.apache.maven.plugins' && plugin.artifactId === 'maven-checkstyle-plugin') {
            targets['checkstyle'] = {
                command: 'mvn checkstyle:check',
                options: { cwd: projectRoot },
                cache: true,
                inputs: ['{projectRoot}/src/**/*'],
                outputs: ['{projectRoot}/target/checkstyle-result.xml'],
                metadata: {
                    technologies: ['maven', 'checkstyle'],
                    description: 'Run Checkstyle code analysis',
                },
            };
            targetGroups.quality.push('checkstyle');
        }

        // SpotBugs plugin
        if (plugin.groupId === 'com.github.spotbugs' && plugin.artifactId === 'spotbugs-maven-plugin') {
            targets['spotbugs'] = {
                command: 'mvn spotbugs:check',
                options: { cwd: projectRoot },
                cache: true,
                inputs: ['{projectRoot}/src/**/*'],
                outputs: ['{projectRoot}/target/spotbugsXml.xml'],
                metadata: {
                    technologies: ['maven', 'spotbugs'],
                    description: 'Run SpotBugs static analysis',
                },
            };
            targetGroups.quality.push('spotbugs');
        }
    }
}

function splitConfigFiles(files: readonly string[]) {
    const pomFiles = files.filter((f) => f.endsWith('pom.xml'));
    const testFiles = files.filter((f) => f.includes('/src/test/'));
    return { pomFiles, testFiles };
}

function getMavenProjectRootToTestFilesMap(
    testFiles: string[],
    projectRoots: string[]
): Record<string, string[]> {
    const map: Record<string, string[]> = {};
    for (const root of projectRoots) {
        map[root] = testFiles.filter((f) => f.startsWith(root));
    }
    return map;
}

