const { createDependencies } = require('./maven-plugin.js');
const { parsePomXml } = require('./parse-pom-xml.js');
const path = require('path');

async function debugDependencies() {
  const context = {
    workspaceRoot: process.cwd()
  };

  // Test with a project that should have dependencies
  const testFile = 'integration-tests/hibernate-orm-panache/pom.xml';

  try {
    console.log('=== Testing POM parsing ===');
    const mavenReport = await parsePomXml(context.workspaceRoot, [testFile]);
    const projectInfo = mavenReport.projects.get(testFile);

    if (projectInfo) {
      console.log('Project found:', projectInfo.artifactId);
      console.log('Dependencies count:', projectInfo.dependencies?.length || 0);
      console.log('Dependencies:');
      projectInfo.dependencies?.forEach(dep => {
        console.log(`  - ${dep.groupId}:${dep.artifactId}:${dep.version || 'inherited'} (scope: ${dep.scope})`);
      });
      console.log();

      console.log('=== Testing coordinate matching ===');
      // Test finding projects by coordinates
      console.log('All projects in report:');
      let count = 0;
      for (const [pomPath, project] of mavenReport.projects) {
        const projectGroupId = project.groupId ?? project.parent?.groupId;
        console.log(`  ${pomPath} -> ${projectGroupId}:${project.artifactId}`);
        count++;
        if (count > 10) {
          console.log('  ... (showing first 10)');
          break;
        }
      }

      console.log('\n=== Testing dependency matching ===');
      if (projectInfo.dependencies) {
        for (const dep of projectInfo.dependencies.slice(0, 5)) {
          let found = false;
          for (const [pomPath, project] of mavenReport.projects) {
            const projectGroupId = project.groupId ?? project.parent?.groupId;
            if (projectGroupId === dep.groupId && project.artifactId === dep.artifactId) {
              console.log(`  MATCH: ${dep.groupId}:${dep.artifactId} -> ${pomPath}`);
              found = true;
              break;
            }
          }
          if (!found) {
            console.log(`  NO MATCH: ${dep.groupId}:${dep.artifactId}`);
          }
        }
      }
    } else {
      console.log('Project not found in maven report');
    }

    console.log('\n=== Testing createDependencies ===');
    const deps = await createDependencies([testFile], {}, context);
    console.log('Dependencies created:', deps.length);
    deps.forEach(dep => {
      console.log(`  ${dep.sourceProjectName} -> ${dep.targetProjectName} (${dep.dependencyType})`);
    });

  } catch (error) {
    console.error('Error:', error.message);
    console.error(error.stack);
  }
}

