export async function createDependencies(
    files: string[],
    options: any,
    context: any
) {
    console.log(`[Maven Plugin] createDependencies called with ${files?.length || 0} files`);

    if (!files || files.length === 0) {
        console.log('[Maven Plugin] No files provided, returning empty dependencies');
        return [];
    }

    const { pomFiles } = splitConfigFiles(files.filter(f => f.endsWith('pom.xml')));
    console.log(`[Maven Plugin] Found ${pomFiles.length} pom.xml files to process`);

    if (pomFiles.length === 0) {
        console.log('[Maven Plugin] No pom.xml files found, returning empty dependencies');
