import { readFileSync } from 'node:fs';
import { join } from 'node:path';
import { parseString } from 'xml2js';
import { promisify } from 'node:util';

const parseXml = promisify(parseString);

export interface MavenPlugin {
  groupId: string;
  artifactId: string;
  version?: string;
  executions?: Array<{
    id?: string;
    phase?: string;
    goals?: string[];
  }>;
  configuration?: any;
}

export interface MavenProjectInfo {
  groupId: string;
  artifactId: string;
  version: string;
  packaging: string;
  modules?: string[];
  dependencies?: Array<{
    groupId: string;
    artifactId: string;
    version?: string;
    scope?: string;
    type?: string;
  }>;
  parent?: {
    groupId: string;
    version: string;
  };
  plugins?: MavenPlugin[];
  properties?: Record<string, string>;
}

export interface MavenReport {
  projects: Map<string, MavenProjectInfo>;
  projectToModules: Map<string, string[]>;
}

export async function parsePomXml(
  workspaceRoot: string,
  pomFiles: string[]
): Promise<MavenReport> {
  const report: MavenReport = {
    projects: new Map(),
    projectToModules: new Map(),
  };

  for (const pomFile of pomFiles) {
    try {
      const pomContent = readFileSync(join(workspaceRoot, pomFile), 'utf-8');
      const parsed = await parseXml(pomContent) as any;
      const project = parsed.project;

      if (!project) {
        continue;
      }

      const projectInfo: MavenProjectInfo = {
        groupId: project.groupId?.[0] || project.parent?.[0]?.groupId?.[0] || '',
        artifactId: project.artifactId?.[0] || '',
        version: project.version?.[0] || project.parent?.[0]?.version?.[0] || '',
        packaging: project.packaging?.[0] || 'jar',
      };

      // Add parent info if present
      if (project.parent?.[0]) {
        projectInfo.parent = {
          groupId: project.parent[0].groupId?.[0] || '',
          version: project.parent[0].version?.[0] || '',
        };
      }

      // Parse modules if present
      if (project.modules?.[0]?.module) {
        projectInfo.modules = project.modules[0].module;
        report.projectToModules.set(pomFile, projectInfo.modules);
      }

      // Parse dependencies if present
      if (project.dependencies?.[0]?.dependency) {
        projectInfo.dependencies = project.dependencies[0].dependency.map(
          (dep: any) => ({
            groupId: dep.groupId?.[0] || '',
            artifactId: dep.artifactId?.[0] || '',
            version: dep.version?.[0],
            scope: dep.scope?.[0] || 'compile',
            type: dep.type?.[0] || 'jar',
          })
        );
      }

      // Parse properties if present
      if (project.properties?.[0]) {
        projectInfo.properties = {};
        Object.keys(project.properties[0]).forEach(key => {
          if (project.properties[0][key]?.[0]) {
            projectInfo.properties![key] = project.properties[0][key][0];
          }
        });
      }

      // Parse plugins from build section
      if (project.build?.[0]?.plugins?.[0]?.plugin) {
        projectInfo.plugins = project.build[0].plugins[0].plugin.map((plugin: any) => {
          const pluginInfo: MavenPlugin = {
            groupId: plugin.groupId?.[0] || '',
            artifactId: plugin.artifactId?.[0] || '',
            version: plugin.version?.[0],
          };

          // Parse executions if present
          if (plugin.executions?.[0]?.execution) {
            pluginInfo.executions = plugin.executions[0].execution.map((exec: any) => ({
              id: exec.id?.[0],
              phase: exec.phase?.[0],
              goals: exec.goals?.[0]?.goal || [],
            }));
          }

          // Parse configuration if present
          if (plugin.configuration?.[0]) {
            pluginInfo.configuration = plugin.configuration[0];
          }

          return pluginInfo;
        });
      }

      report.projects.set(pomFile, projectInfo);
    } catch (e) {
      console.error(`Error parsing POM file ${pomFile}:`, e);
    }
  }

  return report;
}

