export async function createDependencies(
    files: string[],
    options: any,
    context: any
) {
    console.log(`[Maven Plugin] createDependencies called with ${files?.length || 0} files`);

    if (!files || files.length === 0) {
        console.log('[Maven Plugin] No files provided, returning empty dependencies');
        return [];
    }

    const { pomFiles } = splitConfigFiles(files.filter(f => f.endsWith('pom.xml')));
    console.log(`[Maven Plugin] Found ${pomFiles.length} pom.xml files to process`);

    if (pomFiles.length === 0) {
        console.log('[Maven Plugin] No pom.xml files found, returning empty dependencies');
        return [];
    }

    console.log(`[Maven Plugin] Parsing ${pomFiles.length} POM files...`);
    const mavenReport = await parsePomXml(context.workspaceRoot, pomFiles);
    console.log(`[Maven Plugin] Parsed ${mavenReport.projects.size} projects from POMs`);

    const dependencies = [];
    let processedProjects = 0;

    for (const pomFile of pomFiles) {
        const projectInfo = mavenReport.projects.get(pomFile);
        if (!projectInfo) continue;

        processedProjects++;
        if (processedProjects % 100 === 0) {
            console.log(`[Maven Plugin] Processed ${processedProjects}/${pomFiles.length} projects...`);
        }

        const projectRoot = dirname(pomFile);
        const sourceProjectName = `${projectInfo.groupId ?? projectInfo.parent?.groupId}.${projectInfo.artifactId}`;

        // Create runtime dependencies (compile + runtime scope)
        const runtimeDependencies = createProjectDependencies(projectInfo, mavenReport, false);

        for (const targetProject of runtimeDependencies) {
            dependencies.push({
                sourceProjectName,
                targetProjectName: targetProject,
                dependencyType: 'static' as const,
                source: pomFile,
            });
        }

        // Create test dependencies (test scope)
        const testDependencies = createProjectDependencies(projectInfo, mavenReport, true);

        for (const targetProject of testDependencies) {
            // Only add if not already included as a runtime dependency
            if (!runtimeDependencies.includes(targetProject)) {
                dependencies.push({
                    sourceProjectName,
                    targetProjectName: targetProject,
                    dependencyType: 'static' as const,
                    source: pomFile,
                });
            }
        }

        // Create dependencies from parent relationships
        if (projectInfo.parent) {
            const parentProjectName = findProjectByMavenCoordinates(
                projectInfo.parent.groupId,
                '', // Parent might not have specific artifactId
                mavenReport
            );
            if (parentProjectName) {
                dependencies.push({
                    sourceProjectName,
                    targetProjectName: parentProjectName,
                    dependencyType: 'implicit' as const,
                    source: pomFile,
                });
            }
        }

        // Create dependencies from module relationships (parent -> modules)
        if (projectInfo.modules) {
            for (const module of projectInfo.modules) {
                const modulePath = join(projectRoot, module, 'pom.xml');
                const moduleProject = mavenReport.projects.get(modulePath);
                if (moduleProject) {
                    const moduleProjectName = `${moduleProject.groupId ?? moduleProject.parent?.groupId}.${moduleProject.artifactId}`;
                    dependencies.push({
                        sourceProjectName,
                        targetProjectName: moduleProjectName,
                        dependencyType: 'implicit' as const,
                        source: pomFile,
                    });
                }
            }
        }
    }

    console.log(`[Maven Plugin] Created ${dependencies.length} dependencies from ${processedProjects} projects`);
    return dependencies;
}

