  1import model.TargetConfiguration;
  2import org.apache.maven.plugin.logging.Log;
  3import org.apache.maven.project.MavenProject;
  4
  5import java.util.ArrayList;
  6import java.util.LinkedHashSet;
  7import java.util.List;
  8import java.util.Map;
  9import java.util.Set;
 10
 11/**
 12 * Service responsible for calculating target dependencies in Maven projects.
 13 * Handles phase dependencies, cross-module dependencies, and goal dependencies.
 14 */
 15public class TargetDependencyService {
 16
 17    private final Log log;
 18    private final boolean verbose;
 19    private final ExecutionPlanAnalysisService executionPlanAnalysisService;
 20
 21    public TargetDependencyService(Log log, boolean verbose, ExecutionPlanAnalysisService executionPlanAnalysisService) {
 22        this.log = log;
 23        this.verbose = verbose;
 24        this.executionPlanAnalysisService = executionPlanAnalysisService;
 25    }
 26
 27    /**
 28     * Calculate dependencies for a goal target.
 29     * Goals now depend on other goals, not on phases.
 30     */
 31    public List<String> calculateGoalDependencies(MavenProject project, String executionPhase,
 32                                                  String targetName, List<MavenProject> actualDependencies) {
 33        Set<String> dependsOnSet = new LinkedHashSet<>();
 34
 35        String effectivePhase = executionPhase;
 36        if (effectivePhase == null || effectivePhase.isEmpty() || effectivePhase.startsWith("${")) {
 37            effectivePhase = executionPlanAnalysisService.findPhaseForGoal(project, ExecutionPlanAnalysisService.extractGoalFromTargetName(targetName));
 38        }
 39
 40        if (effectivePhase != null && !effectivePhase.isEmpty()) {
 41            // Add goal-to-goal dependencies based on Maven lifecycle ordering
 42            List<String> precedingGoals = getPrecedingGoalsInLifecycle(project, effectivePhase);
 43            dependsOnSet.addAll(precedingGoals);
 44
 45            // Add cross-module dependencies for goals in the same phase across all dependent projects
 46            List<String> crossModuleGoals = getCrossModuleGoalsForPhase(project, effectivePhase, actualDependencies);
 47            dependsOnSet.addAll(crossModuleGoals);
 48        }
 49
 50        return new ArrayList<>(dependsOnSet);
 51    }
 52
 53    /**
 54     * Calculate dependencies for a phase target
 55     */
 56    public List<String> calculatePhaseDependencies(String phase, Map<String, TargetConfiguration> allTargets,
 57                                                   MavenProject project, List<MavenProject> reactorProjects) {
 58        List<String> dependsOn = new ArrayList<>();
 59
 60        // Add dependencies on all goals that belong to this phase
 61        List<String> goalsForPhase = getGoalsForPhase(phase, allTargets);
 62        dependsOn.addAll(goalsForPhase);
 63
 64        return dependsOn;
 65    }
 66
 67    /**
 68     * Get phase dependencies (preceding phases)
 69     */
 70    public List<String> getPhaseDependencies(String phase, MavenProject project) {
 71        List<String> deps = new ArrayList<>();
 72        String precedingPhase = getPrecedingPhase(phase, project);
 73        if (precedingPhase != null) {
 74            deps.add(precedingPhase);
 75        }
 76        return deps;
 77    }
 78
 79    /**
 80     * Get all goals that belong to a specific phase
 81     */
 82    public List<String> getGoalsForPhase(String phase, Map<String, TargetConfiguration> allTargets) {
 83        List<String> goalsForPhase = new ArrayList<>();
 84
 85        for (Map.Entry<String, TargetConfiguration> entry : allTargets.entrySet()) {
 86            String targetName = entry.getKey();
 87            TargetConfiguration target = entry.getValue();
 88
 89            if (target.getMetadata() != null &&
 90                "goal".equals(target.getMetadata().getType()) &&
 91                phase.equals(target.getMetadata().getPhase())) {
 92                goalsForPhase.add(targetName);
 93            }
 94        }
 95
 96        return goalsForPhase;
 97    }
 98
 99
100
101
102    /**
103     * Get all goals from preceding phases in the Maven lifecycle.
104     * This ensures goals depend on other goals, not phases.
105     */
106    public List<String> getPrecedingGoalsInLifecycle(MavenProject project, String currentPhase) {
107        List<String> precedingGoals = new ArrayList<>();
108
109        if (currentPhase == null || currentPhase.isEmpty()) {
110            return precedingGoals;
111        }
112
113        // Get the lifecycle containing this phase
114        org.apache.maven.lifecycle.Lifecycle lifecycle = executionPlanAnalysisService.getLifecycleForPhase(currentPhase);
115        if (lifecycle == null || lifecycle.getPhases() == null) {
116            return precedingGoals;
117        }
118
119        List<String> lifecyclePhases = lifecycle.getPhases();
120        int currentPhaseIndex = lifecyclePhases.indexOf(currentPhase);
121
122        if (currentPhaseIndex <= 0) {
123            return precedingGoals; // No preceding phases
124        }
125
126        // Get all goals from all preceding phases using project's plugin configuration
127        for (int i = 0; i < currentPhaseIndex; i++) {
128            String precedingPhase = lifecyclePhases.get(i);
129            List<String> phaseGoals = getCommonGoalsForPhase(precedingPhase, project);
130            precedingGoals.addAll(phaseGoals);
131        }
132
133        if (verbose && !precedingGoals.isEmpty()) {
134            log.debug("Found " + precedingGoals.size() + " preceding goals for phase '" + currentPhase + "': " + precedingGoals);
135        }
136
137        return precedingGoals;
138    }
139    
140    /**
141     * Get goals for a Maven lifecycle phase by looking up from the project's plugin configuration.
142     * This finds all goals from the project that are bound to the specified phase.
143     */
144    private List<String> getCommonGoalsForPhase(String phase, MavenProject project) {
145        List<String> goals = new ArrayList<>();
146        
147        if (project == null || project.getBuildPlugins() == null) {
148            return goals;
149        }
150        
151        // Go through all plugins and their executions to find goals bound to this phase
152        for (org.apache.maven.model.Plugin plugin : project.getBuildPlugins()) {
153            if (plugin.getExecutions() != null) {
154                for (org.apache.maven.model.PluginExecution execution : plugin.getExecutions()) {
155                    String executionPhase = execution.getPhase();
156                    
157                    // Check if this execution is bound to the phase we're looking for
158                    if (phase.equals(executionPhase) && execution.getGoals() != null) {
159                        for (String goal : execution.getGoals()) {
160                            String pluginGoal = ExecutionPlanAnalysisService.getTargetName(plugin.getArtifactId(), goal);
161                            goals.add(pluginGoal);
162                        }
163                    }
164                }
165            }
166        }
167        
168        return goals;
169    }
170
171    /**
172     * Get goals from the same phase across actual project dependencies.
173     * Returns dependencies in format "nxProjectName:goalName" where nxProjectName is "groupId:artifactId".
174     */
175    public List<String> getCrossModuleGoalsForPhase(MavenProject currentProject, String phase, List<MavenProject> actualDependencies) {
176        List<String> crossModuleGoals = new ArrayList<>();
177
178        if (phase == null || phase.isEmpty() || actualDependencies == null) {
179            return crossModuleGoals;
180        }
181
182        // For each actual dependency project
183        for (MavenProject otherProject : actualDependencies) {
184            if (otherProject != null && !otherProject.equals(currentProject)) {
185                // Get all goals for this phase in the other project
186                List<String> phaseGoals = executionPlanAnalysisService.getGoalsForPhase(otherProject, phase);
187
188                for (String goal : phaseGoals) {
189                    if (goal.contains(":")) {
190                        // Add with Nx project name to create project:goal dependency
191                        String nxProjectName = MavenUtils.formatProjectKey(otherProject);
192                        crossModuleGoals.add(nxProjectName + ":" + goal);
193                    }
194                }
195            }
196        }
197
198        if (verbose && !crossModuleGoals.isEmpty()) {
199            log.debug("Found " + crossModuleGoals.size() + " cross-module project:goal dependencies from actual dependencies for phase '" + phase + "': " + crossModuleGoals);
200        }
201
202        return crossModuleGoals;
203    }
204
205    /**
206     * Get the preceding phase in the Maven lifecycle (supports all lifecycles: default, clean, site)
207     */
208    public String getPrecedingPhase(String phase, MavenProject project) {
209        if (phase == null || phase.isEmpty()) {
210            return null;
211        }
212
213        // Find which lifecycle contains this phase and get its phases
214        org.apache.maven.lifecycle.Lifecycle lifecycle = executionPlanAnalysisService.getLifecycleForPhase(phase);
215        String precedingPhase = null;
216
217        if (lifecycle != null && lifecycle.getPhases() != null) {
218            precedingPhase = findPrecedingPhaseInLifecycle(phase, lifecycle.getPhases());
219        }
220
221        if (precedingPhase != null && verbose) {
222            log.info("Found preceding phase: " + precedingPhase);
223        }
224
225        return precedingPhase;
226    }
227
228    /**
229     * Helper method to find preceding phase within a specific lifecycle
230     */
231    private String findPrecedingPhaseInLifecycle(String phase, List<String> lifecyclePhases) {
232        int currentPhaseIndex = lifecyclePhases.indexOf(phase);
233        if (currentPhaseIndex > 0) {
234            return lifecyclePhases.get(currentPhaseIndex - 1);
235        }
236        return null;
237    }
