  1import org.apache.maven.execution.MavenSession;
  2import org.apache.maven.lifecycle.LifecycleExecutor;
  3import org.apache.maven.lifecycle.MavenExecutionPlan;
  4import org.apache.maven.lifecycle.DefaultLifecycles;
  5import org.apache.maven.plugin.MojoExecution;
  6import org.apache.maven.plugin.logging.Log;
  7import org.apache.maven.project.MavenProject;
  8
  9import java.util.*;
 10import java.util.concurrent.ConcurrentHashMap;
 11import java.util.stream.Collectors;
 12
 13/**
 14 * Service that analyzes Maven execution plans once and caches results for efficient querying.
 15 * This avoids recalculating execution plans multiple times for the same project.
 16 */
 17public class ExecutionPlanAnalysisService {
 18    
 19    private final Log log;
 20    private final boolean verbose;
 21    private final LifecycleExecutor lifecycleExecutor;
 22    private final MavenSession session;
 23    private final DefaultLifecycles defaultLifecycles;
 24    
 25    // Cache to store analysis results per project
 26    private final Map<String, ProjectExecutionAnalysis> analysisCache = new ConcurrentHashMap<>();
 27    
 28    public ExecutionPlanAnalysisService(Log log, boolean verbose, LifecycleExecutor lifecycleExecutor, MavenSession session, DefaultLifecycles defaultLifecycles) {
 29        this.log = log;
 30        this.verbose = verbose;
 31        this.lifecycleExecutor = lifecycleExecutor;
 32        this.session = session;
 33        this.defaultLifecycles = defaultLifecycles;
 34    }
 35    
 36    /**
 37     * Get the LifecycleExecutor for use by other services
 38     */
 39    public LifecycleExecutor getLifecycleExecutor() {
 40        return lifecycleExecutor;
 41    }
 42    
 43    /**
 44     * Get the DefaultLifecycles for use by other services
 45     */
 46    public DefaultLifecycles getDefaultLifecycles() {
 47        return defaultLifecycles;
 48    }
 49    
 50    /**
 51     * Get or compute execution analysis for a project
 52     */
 53    public ProjectExecutionAnalysis getAnalysis(MavenProject project) {
 54        String projectKey = MavenUtils.formatProjectKey(project);
 55        return analysisCache.computeIfAbsent(projectKey, key -> analyzeProject(project));
 56    }
 57    
 58    /**
 59     * Find the phase for a specific goal
 60     */
 61    public String findPhaseForGoal(MavenProject project, String goal) {
 62        if (project == null || goal == null || goal.isEmpty()) {
 63            return null;
 64        }
 65        ProjectExecutionAnalysis analysis = getAnalysis(project);
 66        return analysis.getPhaseForGoal(goal);
 67    }
 68    
 69    
 70    /**
 71     * Get all goals for a specific phase
 72     */
 73    public List<String> getGoalsForPhase(MavenProject project, String phase) {
 74        if (project == null || phase == null) {
 75            return new ArrayList<>();
 76        }
 77        ProjectExecutionAnalysis analysis = getAnalysis(project);
 78        return analysis.getGoalsForPhase(phase);
 79    }
 80    
 81    /**
 82     * Get all lifecycle phases from all lifecycles (default, clean, site)
 83     */
 84    public Set<String> getAllLifecyclePhases() {
 85        Set<String> allPhases = new LinkedHashSet<>();
 86        allPhases.addAll(getLifecyclePhases("default"));
 87        allPhases.addAll(getLifecyclePhases("clean"));
 88        allPhases.addAll(getLifecyclePhases("site"));
 89        return allPhases;
 90    }
 91    
 92    /**
 93     * Get default lifecycle phases (validate, compile, test, package, verify, install, deploy)
 94     */
 95    public List<String> getDefaultLifecyclePhases() {
 96        return getLifecyclePhases("default");
 97    }
 98    
 99    /**
100     * Get clean lifecycle phases (pre-clean, clean, post-clean)
101     */
102    public List<String> getCleanLifecyclePhases() {
103        return getLifecyclePhases("clean");
104    }
105    
106    /**
107     * Get site lifecycle phases (pre-site, site, post-site, site-deploy)
108     */
109    public List<String> getSiteLifecyclePhases() {
110        return getLifecyclePhases("site");
111    }
112    
113    /**
114     * Get the lifecycle that contains the specified phase
115     */
116    public org.apache.maven.lifecycle.Lifecycle getLifecycleForPhase(String phase) {
117        if (defaultLifecycles == null || phase == null) {
118            return null;
119        }
120        
121        try {
122            return defaultLifecycles.getPhaseToLifecycleMap().get(phase);
123        } catch (Exception e) {
124            if (verbose) {
125                log.warn("Could not get lifecycle for phase '" + phase + "': " + e.getMessage());
126            }
127            return null;
128        }
129    }
130    
131    /**
132     * Get all goals that should be completed by the time the specified phase finishes.
133     * This includes goals from all phases up to and including the target phase.
134     */
135    public List<String> getGoalsCompletedByPhase(MavenProject project, String targetPhase) {
136        if (project == null || targetPhase == null) {
137            return new ArrayList<>();
138        }
139        
140        List<String> completedGoals = new ArrayList<>();
141        org.apache.maven.lifecycle.Lifecycle lifecycle = getLifecycleForPhase(targetPhase);
142        
143        if (lifecycle == null) {
144            return completedGoals;
145        }
146        
147        List<String> lifecyclePhases = lifecycle.getPhases();
148        int targetPhaseIndex = lifecyclePhases.indexOf(targetPhase);
149        
150        if (targetPhaseIndex == -1) {
151            return completedGoals;
152        }
153        
154        // Use Set to avoid duplicates
155        Set<String> uniqueGoals = new LinkedHashSet<>();
156        
157        // Get goals from all phases up to and including the target phase
158        for (int i = 0; i <= targetPhaseIndex; i++) {
159            String phase = lifecyclePhases.get(i);
160            List<String> phaseGoals = getGoalsForPhase(project, phase);
161            
162            // Convert to proper plugin:goal format
163            for (String goal : phaseGoals) {
164                if (goal.contains(":")) {
165                    // Goal is already in plugin:goal format
166                    String[] parts = goal.split(":");
167                    if (parts.length >= 3) {
168                        // Format: groupId:artifactId:goal
169                        String groupId = parts[0];
170                        String artifactId = parts[1];
171                        String goalName = parts[2];
172                        uniqueGoals.add(groupId + ":" + artifactId + ":" + goalName);
173                    } else if (parts.length == 2) {
174                        // Format: plugin:goal - convert to full format
175                        String plugin = parts[0];
176                        String goalName = parts[1];
177                        uniqueGoals.add("org.apache.maven.plugins:maven-" + plugin + "-plugin:" + goalName);
178                    }
179                }
180            }
181        }
182        
183        completedGoals.addAll(uniqueGoals);
184        
185        return completedGoals;
186    }
187    
188    
189    /**
190     * Get lifecycle phases for a specific lifecycle ID
191     */
192    private List<String> getLifecyclePhases(String lifecycleId) {
193        if (defaultLifecycles == null || lifecycleId == null) {
194            return new ArrayList<>();
195        }
196
197        try {
198            for (org.apache.maven.lifecycle.Lifecycle lifecycle : defaultLifecycles.getLifeCycles()) {
199                if (lifecycleId.equals(lifecycle.getId()) && lifecycle.getPhases() != null) {
200                    return new ArrayList<>(lifecycle.getPhases());
201                }
202            }
203            return new ArrayList<>();
204
205        } catch (Exception e) {
206            if (verbose) {
207                log.warn("Could not access Maven lifecycle '" + lifecycleId + "': " + e.getMessage());
208            }
209            return new ArrayList<>();
210        }
211    }
212    
213    /**
214     * Analyze a project's execution plans and cache the results
215     */
216    private ProjectExecutionAnalysis analyzeProject(MavenProject project) {
217        if (verbose) {
218            log.debug("Analyzing execution plans for project: " + project.getArtifactId());
219        }
220        
221        ProjectExecutionAnalysis analysis = new ProjectExecutionAnalysis();
222        
223        // Use actual lifecycle phases from Maven's lifecycle definitions
224        Set<String> lifecyclePhases = getAllLifecyclePhases();
225        
226        for (String phase : lifecyclePhases) {
227            try {
228                if (lifecycleExecutor != null && session != null) {
229                    MavenExecutionPlan executionPlan = lifecycleExecutor.calculateExecutionPlan(session, phase);
230                    analysis.addExecutionPlan(phase, executionPlan);
231                    
232                    if (verbose) {
233                        log.debug("Analyzed " + executionPlan.getMojoExecutions().size() + 
234                                 " executions for phase: " + phase);
235                    }
236                } else {
237                    if (verbose) {
238                        log.warn("LifecycleExecutor or session is null, skipping phase: " + phase);
239                    }
240                }
241                
242            } catch (Exception e) {
243                if (verbose) {
244                    log.warn("Could not calculate execution plan for " + phase + ": " + e.getMessage());
245                }
246            }
247        }
248        
249        if (verbose) {
250            log.debug("Completed analysis for " + project.getArtifactId() + 
251                     ": " + analysis.getAllPhases().size() + " phases, " + 
252                     analysis.getAllGoals().size() + " goals");
253        }
254        
255        return analysis;
256    }
257    
