/**
 * Get goals from the same phase across actual project dependencies.
 * Returns TargetDependencyConfig objects for more accurate cross-module dependencies.
 */
public List<TargetDependencyConfig> getCrossModuleGoalsForPhase(MavenProject currentProject, String phase, List<MavenProject> actualDependencies) {
    List<TargetDependencyConfig> crossModuleGoals = new ArrayList<>();

    if (phase == null || phase.isEmpty() || actualDependencies == null) {
        return crossModuleGoals;
    }

    // For each actual dependency project
    for (MavenProject otherProject : actualDependencies) {
        if (otherProject != null && !otherProject.equals(currentProject)) {
            // Get all goals for this phase in the other project
            List<String> phaseGoals = executionPlanAnalysisService.getGoalsForPhase(otherProject, phase);

            for (String goal : phaseGoals) {
                if (goal.contains(":")) {
                    // Create TargetDependencyConfig with project specification
                    String nxProjectName = MavenUtils.formatProjectKey(otherProject);
                    TargetDependencyConfig dependency = new TargetDependencyConfig(goal, nxProjectName);
                    dependency.setParams("ignore"); // Don't forward parameters for cross-module dependencies
                    crossModuleGoals.add(dependency);
                }
            }
        }
    }

    if (verbose && !crossModuleGoals.isEmpty()) {
        log.debug("Found " + crossModuleGoals.size() + " cross-module TargetDependencyConfig objects from actual dependencies for phase '" + phase + "'");
    }

    return crossModuleGoals;
}

