            case "jar" -> "Create JAR";
            case "war" -> "Create WAR";
            case "site" -> "Generate site documentation";
            case "javadoc" -> "Generate Javadoc";
            case "enforce" -> "Enforce build rules";
            case "create" -> "Create build metadata";
            default -> pluginName + " " + goal;
        };
    }
