  1import model.TargetConfiguration;
  2import model.TargetMetadata;
  3import org.apache.maven.execution.MavenSession;
  4import org.apache.maven.lifecycle.LifecycleExecutor;
  5import org.apache.maven.lifecycle.MavenExecutionPlan;
  6import org.apache.maven.model.Plugin;
  7import org.apache.maven.model.PluginExecution;
  8import org.apache.maven.plugin.MojoExecution;
  9import org.apache.maven.plugin.logging.Log;
 10import org.apache.maven.project.MavenProject;
 11
 12import java.util.LinkedHashSet;
 13
 14import java.io.File;
 15import java.util.ArrayList;
 16import java.util.Arrays;
 17import java.util.LinkedHashMap;
 18import java.util.List;
 19import java.util.Map;
 20import java.util.Set;
 21
 22/**
 23 * Service responsible for generating Nx targets from Maven project configuration.
 24 * Handles both phase targets and plugin goal targets.
 25 */
 26public class TargetGenerationService {
 27    
 28    private final Log log;
 29    private final boolean verbose;
 30    private final MavenSession session;
 31
 32    public TargetGenerationService(Log log, boolean verbose, MavenSession session) {
 33        this.log = log;
 34        this.verbose = verbose;
 35        this.session = session;
 36    }
 37
 38    /**
